const mapDispatchToProps = dispatch => {
  return bindActionCreators(
    {
      getCommonApi,
      // deleteProject,
      // getProject
    },
    dispatch
  );
};



export const Details =withTranslation()( connect(
  null,
  mapDispatchToProps
)(DetailsClass));
