        page = page - 1;
        await this.setState({
            page
        })
        if (page > 0) {
            this.getStaffList();
        }
    }

    handleAddstaff = (item) => {
        let { tstaffList, formFields, cartData, postFields } = this.state;
        let data = {
            helper_id: item.id
        }
        if (postFields.work_point && postFields.times) {
            this.props.commonCreateApi(`tmpitemhelper/?cartid=${this.props.cartId}&workcommpoints=${postFields.work_point}&times=${postFields.times}`, data).then(() => {
                this.getCart();
            })
        } else {
            this.setState({ showPostError: true })
        }
    }

    handleUpdatestaff = async(event, item, index) => {
        let { tstaffList, formFields, cartData, updateFields,postFields } = this.state;
        let workpoint=parseFloat(postFields.work_point);
        let userEnteredValue=parseFloat(event.target.value);
        if(userEnteredValue>workpoint)
        {
            return;
        }
        tstaffList[index][event.target.name] = event.target.value;
        this.setState({
            tstaffList
        })
        let data = {}
        if(event.target.name==="appt_fr_time"){
            data = {
                appt_fr_time: event.target.value,
                add_duration: formFields['add_duration'],
                wp1:item.wp1,
            }
        }
        if(event.target.name==="add_duration"){
            data = {
                appt_fr_time: item.appt_fr_time,
                add_duration: event.target.value,
                wp1:item.wp1,
            }
        }
        if(event.target.name==="wp1")
        {
            data = {
                appt_fr_time: formFields['appt_fr_time'],
                add_duration: formFields['add_duration'],
                wp1:item.wp1,
            }  
        }
        
        // if (updateFields.Source && updateFields.Room) {
            this.props.commonPatchApi(`tmpitemhelper/${item.id}/?Room_Codeid=${updateFields.Room}&Source_Codeid=${updateFields.Source}&new_remark=${updateFields.new_remark}`, data).then(() => {
                this.getCart();
            })
        // } else {
        //     this.setState({ showUpdateError: true })
        // }
    }

    handleClearLine = () => {
        this.props.commonDeleteApi(`tmpitemhelper/delete/?clear_all=0&cartid=${this.props.cartId}`).then(()=>{
            this.getCart();
        })
    }

    handleClearAll = () => {
        this.props.commonDeleteApi(`tmpitemhelper/delete/?clear_all=1&cartid=${this.props.cartId}`).then(()=>{
            let { formFields, postFields } = this.state;
            formFields["work_point"] = "";
            postFields['times'] = "";
            this.setState({
                formFields,
                postFields
            });
            this.getCart();
           
        })
    }
    handleChangeInput = async(event, item, index) => {
        let tstaffList=[];
        tstaffList= this.state.tstaffList;
        tstaffList[index].wp1 = event.target.value;
        // update state
        this.setState({
            tstaffList,
        }, () => {
            this.handleUpdatestaff(event, item, index);
        });
       
    };

    handleSelect_Staff = async(staff) => {
        let { tstaffList, formFields, cartData, postFields } = this.state;
        let data = {
            helper_id: staff.id
        }
        if (postFields.work_point && postFields.times) {
            this.props.commonCreateApi(`tmpitemhelper/?cartid=${this.props.cartId}&workcommpoints=${postFields.work_point}&times=${postFields.times}`, data).then(() => {
                this.getCart1();
            })
        } else {
            this.setState({ showPostError: true })
        }
     

    }
    getCart1 = () => {
        let { roomList, sourceList, staffList, cartData, duration, tstaffList } = this.state;
        this.props.getCommonApi(`tmpitemhelper/?cartid=${this.props.cartId}`).then((key) => {
            // let { status, data } = key;
            // if (status === 200) {
            cartData = key;
            tstaffList = key.data;
            this.setState({ cartData, tstaffList }, () => {
                this.checkFirstArrayAndUpdateTiming();
            });
            this.getDataFromRes(key)
            // }
        })

    }
    checkFirstArrayAndUpdateTiming = () => {
        

        let { tstaffList, cartData, updateFields } = this.state;
        var item =tstaffList[0];
        let { Room_Codeid, Source_Codeid, new_remark } = updateFields;
        var today = new Date(),

        time = today.getHours() + ':' + today.getMinutes();
        let data = {}

            data = {
                appt_fr_time: this.roundTime(time, 10),
                add_duration: cartData.value.add_duration,
                wp1: item.wp1
            }
  
            this.props.commonPatchApi(`tmpitemhelper/${item.id}/?Room_Codeid=${updateFields.Room}&Source_Codeid=${updateFields.Source}&new_remark=${updateFields.new_remark}`, data).then(() => {
                this.getCart();
            })
     
    }
    roundTime = (time, minutesToRound) => {

        let [hours, minutes] = time.split(':');
        hours = parseInt(hours);
        minutes = parseInt(minutes);
    
        // Convert hours and minutes to time in minutes
        time = (hours * 60) + minutes; 
    
        let rounded = Math.round(time / minutesToRound) * minutesToRound;
        let rHr = ''+Math.floor(rounded / 60)
        let rMin = ''+ rounded % 60
    
        return rHr.padStart(2, '0')+':'+rMin.padStart(2, '0')
    }
    render() {
        let { staffList = [], tstaffList = [], roomList, sourceList, formFields, headerDetails, cartData, postFields, updateFields, duration,startDuration } = this.state;
        let { value = {}, data = {} } = cartData;
        let { Item, Price, work_point } = value;
        return (
