                </div>
                <div className="col-6 mb-2">
                    <label className="text-left text-black common-label-text ">
                        Item
                    </label>
                    <div className="input-group mb-2">
                        {Item}
                    </div>
                    <label className="text-left text-black common-label-text ">
                        Price
                    </label>
                    <div className="input-group mb-2">
                        {Price}
                    </div>
                    <label className="text-left text-black common-label-text ">
                        Work Point
                    </label>
                    <div className="input-group">
                        <NormalInput
                            value={postFields.work_point}
                            name="work_point"
