                            disabled={true}
                        />
                        
                    </div>
                    <label className="text-left text-black common-label-text ">
                        Sessions
                    </label>
                    <div className="input-group">
                        <NormalInput
                            value={postFields.times}
                            name="times"
                            onChange={this.handlePostChange}
                            className={`customer-name ${!postFields.times ? "required":""}`}
                            disabled
                        />
                    </div>
                </div>
                <div className="col-6 mb-2">

                 
                </div>
                
                <div className={`col-12 cart-item emp-image`}>
                        <div className={`staff-listing d-flex emp-list`}>
                            <div className="forward-button cursor-pointer" onClick={this.handleBack}>
                                <svg width="6" height="10" viewBox="0 0 6 10" fill="none" xmlns="http://www.w3.org/2000/svg">
                                    <path d="M5 0.5L1 5L5 9.5" stroke="#888888" stroke-linecap="round" stroke-linejoin="round" />
                                </svg>
                            </div>
                            {
                                staffList.length>0 ? staffList.map((staff) => {
                                    return (
                                    <div className="mx-1 staff-list cursor-pointer emp" key={staff.id} onClick={() => this.handleSelect_Staff(staff)}>
                                        <img className="img" src={staff.emp_pic} alt=''/>
                                        <p>{staff.display_name}</p>
                                    </div>
                                    )
                                }):""
                            }

                        <div className="back-button cursor-pointer" onClick={this.handleNext}>
                            <svg width="5" height="10" viewBox="0 0 5 10" fill="none" xmlns="http://www.w3.org/2000/svg">
                                <path d="M0.5 9.5L4.5 5L0.5 0.5" stroke="#888888" stroke-linecap="round" stroke-linejoin="round" />
                            </svg>
                        </div>
                    </div>
                </div>
                <div className={`col-12 cart-item`}>
                    <div className={`item-list`}>

                        <div className="table">
                            <TableWrapper
                                headerDetails={headerDetails}
                                queryHandler={this.handlePagination}
                            // pageMeta={pageMeta}
                            // isEmpty={tstaffList.length === 0 ? true:false}
                            >
                               
                                {tstaffList.length > 0 ? tstaffList.map((item, index) => {
                                    return (
                                        <tr key={index}>

                                            <td className="position-relative status-type"><span className={``}></span><div className="d-flex align-items-center justify-content-center  fs-20">{item.helper_name}</div></td>
                                            <td><div className="d-flex align-items-center justify-content-center">
                                            <td >
                                                <NormalInput 
                                                    onChange={(event)=>this.handleUpdatestaff(event, item, index)}
                                                    name="wp1"
                                                    type="number"
                                                    min="0"
                                                    max={postFields.work_point}
                                                    value={item.wp1} />
                                            </td>
                                                </div></td>

                                            <td>
                                            {this.state.hideNotNeededControls=="true" &&
                                                <div className={`${tstaffList.length > 0 ? "d-flex":"display-none"} align-items-center justify-content-center`}>
                                                    <div className="input-group">
                                                        <NormalSelect
                                                            // placeholder="Enter here"
                                                            options={startDuration}
                                                            value={item.appt_fr_time}
                                                            name="appt_fr_time"
                                                            onChange={(event)=>this.handleUpdatestaff(event, item, index)}
                                                            className="customer-name py-0"
                                                        />
                                                    </div>
                                                </div>
                                            }
                                            </td>
                                            <td>
                                            {
                                            this.state.hideNotNeededControls=="true" &&
                                                <div className="d-flex align-items-center justify-content-center">{item.appt_to_time}</div>
                                            } 
                                            </td>
                                          
                                            <td>
                                                                                                
                                            { tstaffList.length > 0 ?
                                                <div className={`${tstaffList.length > 0 ? "d-flex":"display-none"} align-items-center justify-content-center`}>
                                                     {
                                            this.state.hideNotNeededControls=="true" &&
                                                    <div className="input-group">
                                                        <NormalSelect
                                                            // placeholder="Enter here"
                                                            options={duration}
                                                            value={item.add_duration}
                                                            name="add_duration"
                                                            onChange={(event)=>this.handleUpdatestaff(event, item, index)}
                                                            className="customer-name p-0"
                                                        />
                                                    
                                                    </div>
                                                    }
                                                </div>:""
                                }
                                            </td>
                                        </tr>
                                    )
                                }) : ""
                                }

                            </TableWrapper>
                        </div>
                    </div>
                </div>
                <div className="col-12 mb-2">
                {this.state.hideNotNeededControls=="true" &&
                    <div className="row">
                   <div className="col-4">
                    <label className="text-left text-black common-label-text ">
                        Room
                    </label>

                    <div className="input-group">
                    <NormalSelect
                            // placeholder="Enter here"
                            options={roomList}
                            value={updateFields.Room}
                            name="Room"
                            onChange={this.handleUpdateChange}
                            className="customer-name py-0"
                            // disabled={formFields.Room}
                        />

                    </div>
                    </div>
                    <div className="col-4">
                    <label className="text-left text-black common-label-text ">
                        Source
                    </label>

                    <div className="input-group">
                    <NormalSelect
                            // placeholder="Enter here"
                            options={sourceList}
                            value={updateFields.Source}
                            name="Source"
                            onChange={this.handleUpdateChange}
                            className="customer-name py-0"
                            // disabled={formFields.Source}
                        />
                    </div>
                    </div>

                    <div className="col-4">
                    <label className="text-left text-black common-label-text ">
