    let { basicApptDetail, t } = this.props;
    return (
      <div className="row new-cart">
        <div className={`col-md-7 col-sm-7 col-12 cart-item`}>
          <div className={`item-list`}>
            <div className="row">
              <div className="input-group col-md-3 col-12 my-2">
