      // getCustomer,
      getCommonApi,
      updateForm,
      commonDeleteApi,
      commonPatchApi,
      commonCreateApi,
      commonUpdateApi,
    },
    dispatch
  );
};

export const CartNew = withTranslation()(
  connect(mapStateToProps, mapDispatchToProps)(CartNewClass)
);
