import React, { Component } from "react";
import {
  NormalButton,
  NormalCheckbox,
  NormalInput,
  NormalDate,
} from "component/common";
import "./style.scss";
import { withTranslation } from "react-i18next";
import { AiOutlineDoubleRight } from "react-icons/ai";
import {
  NewItemSupplies,
  ItemSupplies,
  updateCommon,
} from "redux/actions/Backend";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { Toast } from "service/toast";
import moment from "moment";

export default class EditVendordetailsClass extends Component {
  state = {
    itemId: null,
    Ac_code: null,
    sup_att: null,
    sup_name: null,
    address1: null,
    address2: null,
    address3: null,
    postcode: null,
    city: null,
    cstate: null,
    country: null,
    tele_no: null,
    fax_no: null,
    maddress1: null,
    maddress2: null,
    maddress3: null,
    mpostcode: null,
    mcity: null,
    mcstate: null,
    mcountry: null,
    mtele_no: 0,
    mfax_no: 0,
    checked: true,
    slpdate: new Date(),
  };

  componentDidMount = () => {
    let { itemId } = this.state;
