  Addnew = async () => {
    let {
      Ac_code,
      sup_name,
      sup_att,
      slpdate,
      address1,
      address2,
      address3,
      city,
      cstate,
      country,
      tele_no,
      fax_no,
      postcode,
      maddress1,
      maddress2,
      maddress3,
      mcity,
      mcstate,
      mcountry,
      mtele_no,
      mfax_no,
      checked,
      mpostcode,
    } = this.state;
    if (sup_name == null || slpdate == null) {
      Toast({
        type: "error",
        message: "Please check required field",
      });
    } else {
      let newequiment = {
        splyCode: this.state.itemId,
        supplydesc: sup_name,
        splyDate: moment(slpdate).format("YYYY-MM-DD") + "T00:00:00.000Z",
        splyAttn: sup_att,
        splyIc: "",
        splyType: "",
        splyAddr1: address1,
        splyAddr2: address2,
        splyAddr3: address3,
        splyPoscd: postcode,
        splyState: cstate,
        splyCity: city,
        splyCntry: country,
        splymaddr1: maddress1,
        splymaddr2: maddress2,
        splymaddr3: maddress3,
        splymposcd: mpostcode,
        splymstate: mcstate,
        splymcity: mcity,
        splymcntry: mcountry,
        splyTelno: tele_no,
        splyFaxno: fax_no,
        splyRemk1: "",
        splyRemk2: "",
        splyRemk3: "",
        splyTerm: mtele_no,
        splyLimit: 0,
        splyBal: 0,
        splyactive: checked,
        splyComm: mfax_no,
        firstName: "",
        netseq: 0,
        createUser: "",
        createDate: new Date(),
        accountNumber: Ac_code,
        numberOfOpenPOs: 0,
        numberOfTotalPOs: 0,
      };
      await this.props
        .updateCommon(
          `ItemSupplies/update?where=%7B%22splyCode%22%3A%20%22${this.state.itemId}%22%7D

    `,
          newequiment
        )
        .then((data) => {
          Toast({
            type: "success",
            message: "Updated Successfully",
          });
          this.handleimput();
        })
        .catch((e) => console.log(e));
    }
  };

