import { AuthActionType, AppointmentActionType } from 'redux/actions/actionType';
import { addQuery } from 'service/helperFunctions';
import { appointment } from '../../service/apiVariables';

// create appointment postmethod
export const CreateAppointment = body => (dispatch, getState, { api, Toast }) => {
  return new Promise((resolve, reject) => {
    api({ ...appointment.addAppointment, body })
      .then((response) => {
        resolve(response);
        let { message, status, data } = response;
        if(status===201){
          dispatch({ type: AppointmentActionType.appointmentDetail, payload: data });
          Toast({ type: 'success', message });
        } else {
          reject(Toast({ type: 'error', message }));
        }
      })
      .catch(({ message }) => {
        reject(Toast({ type: 'error', message }));
      });
  });
};

// create treatment post method
export const CreateTreatment = body => (dispatch, getState, { api, Toast }) => {
  return new Promise((resolve, reject) => {
    api({ ...appointment.addTreatment, body })
      .then((response) => {
        resolve(response);
        let { message, status, data } = response;
        if(status===200){
          dispatch({ type: AppointmentActionType.getTreatmentDetail, payload: data });
          Toast({ type: 'success', message });
        } else {
          reject(Toast({ type: 'error', message }));
        }
      })
      .catch(({ message }) => {
        reject(Toast({ type: 'error', message }));
      });
  });
};

