import React, { Component } from "react";
import {
  NormalButton,
  NormalSelect,
  NormalInput,
  NormalDate,
  NormalModal,
} from "component/common";
import { withTranslation } from "react-i18next";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import {
  ItemDivs,
  ItemDepts,
  ItemBrands,
  ItemRanges,
} from "redux/actions/Backend";
import {
  Amountapplicable,
  getAmountapplicable,
} from "redux/actions/Commission";
import { CredentialConfirmation } from "./credentialConfirmation";
import closeIcon from "assets/images/close.png";

export default class CommissionClass extends Component {
  state = {
    option: [
      { label: "Choose option", value: 10 },
      { label: "Choose option", value: 25 },
      { label: "Choose option", value: 50 },
      { label: "Choose option", value: 100 },
    ],
    commission_type: [
      { label: "commission", value: 1 },
      { label: "Incentive", value: 2 },
    ],
    com_type: 1,
    count: 10,
    pro_name: "",
    F_date: new Date(),
    T_date: new Date(),
    max_range: "",
    min_range: "",
    commission: "",
    incentive: "",
    division: "",
    dept: "",
    brand: "",
    range: "",
    period: "",
    item: "",
    level: "",
    Division: [],
    depts: [],
    deptsval: "",
    rangeoption: [],
    brandlist: [],
    sales_amt: "",
    service_amt: "",
    sales_point: "",
    service_point: "",
    fees: false,
    commissiondetails: [
      {
        range_max: "",
        range_min: "",
        commission: "",
        incentive: "",
      },
    ],
    isLoginConfirmation: false,
  };

  componentDidMount = () => {
    this.Listofcommission({});
    this.Listofmenu({});
    this.listofbrand({});
  };

  handleChange = ({ target: { value, name } }) => {
    let { F_date, T_date } = this.state;
    console.log(value, name);
    if (name == "fromdate") {
      F_date = value;
    }
    if (name == "todate") {
      T_date = value;
    }
    this.setState({ F_date, T_date });
  };
  handletype = ({ target: { value, name } }) => {
    let {
      com_type,
      min_range,
      max_range,
      commission,
      incentive,
      Division,
      dept,
      brand,
      range,
      pro_name,
      period,
      item,
      divisionval,
      level,
      sales_amt,
      sales_point,
      service_amt,
      service_point,
    } = this.state;
    if (name == "com_type") {
      com_type = value;
    }
    if (name == "min_range") {
      min_range = value;
    }
    if (name == "max_range") {
      max_range = value;
    }
    if (name == "commission") {
      commission = value;
    }
    if (name == "incentive") {
      incentive = value;
    }
    if (name == "division") {
      divisionval = value;
      this.listofdept(divisionval);
      if (divisionval == "3") {
        this.Listofitemrange();
      }
    }
    if (name == "dept") {
      dept = value;
    }
    if (name == "brand") {
      brand = value;
      this.Listofitemrange();
    }
    if (name == "range") {
      range = value;
    }
    if (name == "pro_name") {
      pro_name = value;
    }
    if (name == "period") {
      period = value;
    }
    if (name == "item") {
      item = value;
    }
    if (name == "level") {
      level = value;
    }
    if (name == "sales_amt") {
      sales_amt = value;
    }
    if (name == "sales_point") {
      sales_point = value;
    }
    if (name == "service_amt") {
      service_amt = value;
    }
    if (name == "service_point") {
      service_point = value;
    }
    this.setState({
      com_type,
      min_range,
      max_range,
      commission,
      incentive,
      Division,
      dept,
      brand,
      range,
      item,
      period,
      pro_name,
      level,
      sales_amt,
      sales_point,
      service_amt,
      service_point,
    });
  };
  handleadditonal() {
    this.setState({
      fees: !this.state.fees,
    });
  }

  Listofcommission = async () => {
    let newreason = {
      employe_level_id: 22,
      profile_name: "Saravana",
      period: 1,
    };
    await this.props.Amountapplicable(newreason).then((data) => {
      console.log("Commsiis");
    });
  };

  Listofmenu = async () => {
    let { Division } = this.state;
    Division = [];
    await this.props.ItemDivs().then((res) => {
      for (let key of res) {
        Division.push({
          value: key.itmCode,
          label: key.itmDesc,
          id: key.itmId,
        });
      }
      console.log(Division);
      this.setState({
        Division,
      });
      console.log(Division.length);
    });
  };

  listofdept = async (div) => {
    let { depts } = this.state;
    depts = [];
    console.log(this.state.stockdivision);
    await this.props.ItemDepts().then((res) => {
      if (div == "1") {
        for (let key of res) {
          if (key.isRetailproduct == true) {
            depts.push({
              value: key.itmCode,
              label: key.itmDesc,
              id: key.itmId,
            });
          }
        }
        console.log(depts);
      }
      if (div == 2) {
        for (let key of res) {
          if (key.isSalonproduct == true) {
            depts.push({
              value: key.itmCode,
              label: key.itmDesc,
              id: key.itmId,
            });
          }
        }
        console.log(depts);
      }
      if (div == 3) {
        for (let key of res) {
          if (key.isService == true) {
            depts.push({
              value: key.itmCode,
              label: key.itmDesc,
              id: key.itmId,
            });
          }
        }
        console.log(depts);
      }
      if (div == 4) {
        for (let key of res) {
          if (key.isVoucher == true) {
            depts.push({
              value: key.itmCode,
              label: key.itmDesc,
              id: key.itmId,
            });
          }
        }
        console.log(depts);
      }
      if (div == 5) {
        for (let key of res) {
          if (key.isPrepaid == true) {
            depts.push({
              value: key.itmCode,
              label: key.itmDesc,
              id: key.itmId,
            });
          }
        }
        console.log(depts);
      }

      console.log(depts);
      this.setState({
        depts,
      });
      console.log(depts.length);
    });
  };

  Listofitemrange = async () => {
    let { rangeoption } = this.state;
    rangeoption = [];
    await this.props.ItemRanges().then((res) => {
      if (this.state.stockdivision == "3") {
        for (let key of res) {
          console.log(this.state.dept);
          console.log(key.itmDept);
          if (this.state.deptsval == key.itmDept) {
            rangeoption.push({
              value: key.itmCode,
              label: key.itmDesc,
              id: key.itmId,
            });
          }
        }
      } else {
        for (let key of res) {
          if (this.state.brand == key.itmBrand) {
            rangeoption.push({
              value: key.itmCode,
              label: key.itmDesc,
              id: key.itmId,
            });
          }
        }
      }

      console.log(rangeoption);
      this.setState({
        rangeoption,
      });
      console.log(rangeoption.length);
    });
  };

  listofbrand = async () => {
    let { brandlist } = this.state;
    brandlist = [];
    await this.props.ItemBrands().then((res) => {
      for (let key of res) {
        brandlist.push({
          value: key.itmCode,
          label: key.itmDesc,
          id: key.itmId,
        });
      }
      console.log(brandlist);
      this.setState({
        brandlist,
      });
      console.log(brandlist.length);
    });
  };

  handlecommissionarray = () => {
    let { commissiondetails } = this.state;
    commissiondetails.push({
      range_max: "",
      range_min: "",
      commission: "",
      incentive: "",
    });
    this.setState({
      commissiondetails,
    });
  };

  handledelete = (index) => {
    let { commissiondetails } = this.state;
    if (index > 0) {
      delete commissiondetails[index];
    }

    this.setState({
      commissiondetails,
    });
  };

  handleincentivetextbox = (event, item, index) => {
    if (Number(event.target.value) > 0) {
      let { commissiondetails } = this.state;
      commissiondetails[index]["incentive"] = event.target.value;
      this.setState({ commissiondetails });
      console.log(commissiondetails);
    }
  };
  handlecommissiontextbox = (event, item, index) => {
    if (Number(event.target.value) > 0) {
      let { commissiondetails } = this.state;
      commissiondetails[index]["commission"] = event.target.value;
      this.setState({ commissiondetails });
      console.log(commissiondetails);
    }
  };
  handlerangemaxtextbox = (event, item, index) => {
    if (Number(event.target.value) > 0) {
      let { commissiondetails } = this.state;
      commissiondetails[index]["range_max"] = event.target.value;
      this.setState({ commissiondetails });
      console.log(commissiondetails);
    }
  };
  handlerangemintextbox = (event, item, index) => {
    if (Number(event.target.value) > 0) {
      let { commissiondetails } = this.state;
      commissiondetails[index]["range_min"] = event.target.value;
      this.setState({ commissiondetails });
      console.log(commissiondetails);
    }
  };

  handleLoginConfirmationDialog = () => {
    let { isLoginConfirmation } = this.state;
    this.setState((prevState) => ({
      isLoginConfirmation: !prevState.isLoginConfirmation,
    }));
  };

  handleauthentication = (user, pass) => {
    console.log(user, pass);
  };
  render() {
    let { t } = this.props;
    let {
      option,
      com_type,
      commission_type,
      count,
      level,
      F_date,
      T_date,
      max_range,
      min_range,
      commission,
      incentive,
      Division,
      dept,
      range,
      brand,
      pro_name,
      period,
      item,
      divisionval,
      deptsval,
      depts,
      brandlist,
      rangeoption,
      sales_amt,
      sales_point,
      service_amt,
      commissiondetails,
      service_point,
      isLoginConfirmation,
    } = this.state;
    return (
      <div className="container-fluid commission">
        <div className="d-flex justify-content-between">
