                <span className="star">*</span>
                <div className="d-flex">
                  <div className="input-group">
                    <NormalDate
                      value={F_date}
                      name="fromdate"
                      type="date"
                      onChange={this.handleChange}
                      showDisabledMonthNavigation
                    />
