                    <div className="col-2">
                      <span>{t("Target range max")}</span>
                      <span className="star">*</span>
                      <div className="d-flex">
                        <div className="input-group">
                          <NormalInput
                            onChange={(event) =>
                              this.handlerangemaxtextbox(event, x, i)
                            }
                            value={x.range_max}
                            name="max_range"
                            placeholder="Max range"
                            type="number"
                          />
                        </div>
                      </div>
                    </div>
                    <div className="col-2">
