                <span className="star">*</span>
                <div className="d-flex">
                  <div className="input-group">
                    <NormalSelect
                      options={depts}
                      value={deptsval}
                      onChange={this.handletype}
                      name="dept"
                    />
                  </div>
                </div>
              </div>
              <div className="col-2">
                <span>{t("Brand")}</span>
                <span className="star">*</span>
                <div className="d-flex">
                  <div className="input-group">
                    <NormalSelect
                      options={brandlist}
                      value={brand}
                      onChange={this.handletype}
                      name="brand"
                    />
                  </div>
                </div>
              </div>
              <div className="col-2">
                <span>{t("Range")}</span>
                <span className="star">*</span>
                <div className="d-flex">
                  <div className="input-group">
                    <NormalSelect
                      options={rangeoption}
                      value={range}
                      onChange={this.handletype}
                      name="range"
                    />
                  </div>
                </div>
              </div>
            </div>
          </>
