                <div className="d-flex">
                  <div className="input-group col-4">
                    <NormalInput
                      onChange={this.handletype}
                      value={sales_amt}
                      name="sales_amt"
                      type="number"
                    />
                  </div>
                  <span>{t("* Total sales Amount")}</span>
                </div>
              </div>
              <div className="col-2"></div>
              <div className="col-5 d-flex">
