                <div className="d-flex">
                  <div className="input-group col-4">
                    <NormalInput
                      onChange={this.handletype}
                      value={sales_point}
                      name="sales_point"
                      type="number"
                    />
                  </div>
                  <span>{t("% Total sales Amount of course")}</span>
                </div>
              </div>
              <div className="col-2"></div>
              <div className="col-5 d-flex">
                <span className="col-3">{t("Points")}</span>
                <div className="d-flex">
                  <div className="input-group col-4">
                    <NormalInput
                      onChange={this.handletype}
                      value={service_point}
                      name="service_point"
                      type="number"
                    />
                  </div>
                  <span>{t("% Total sales Amount of course")}</span>
                </div>
              </div>
