import React from "react";
import { NormalButton } from "component/common";
import { InputSearch, TableWrapper } from "component/common";
import { Link } from "react-router-dom";
import "./style.scss";
import {
  getCustomerPlus,
  getCustomerPlusSettings,
} from "redux/actions/customerPlus";
import { updateForm } from "redux/actions/common";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import _ from "lodash";
import { withTranslation } from "react-i18next";

export class ListCustomerPlusClass extends React.Component {
  state = {
    headerDetails: [],
    customerList: [],
    originalCustomerList: [],
    search: "",
    meta: {},
    active: false,
    currentIndex: -1,
    isLoading: true,
    isMounted: true,
  };
  componentWillUnmount() {
    this.state.isMounted = false;
  }

  updateState = (data) => {
    if (this.state.isMounted) this.setState(data);
  };

  componentDidMount = () => {
    this.getCustomerPlus({});
  };

  handleClick = (key) => {
    if (!this.state.active) {
      document.addEventListener("click", this.handleOutsideClick, false);
    } else {
      document.removeEventListener("click", this.handleOutsideClick, false);
    }

    this.updateState((prevState) => ({
      active: !prevState.active,
      currentIndex: key,
    }));
  };

  handleOutsideClick = (e) => {
    if (this.node != null) {
      if (this.node.contains(e.target)) {
        return;
      }
    }
    this.handleClick();
  };

  getCustomerPlus = async (data) => {
    this.updateState({ isLoading: true });
    let { search, headerDetails } = this.state;
    let { page = 1, limit = 10 } = data;
    if (headerDetails.length == 0) {
      await this.props.getCustomerPlusSettings();
      let { settings } = this.props;
      for (let {
        display_field_name,
        field_name,
        visible_in_listing,
      } of settings) {
        if (visible_in_listing)
          headerDetails.push({
            label: display_field_name,
            field: field_name,
            sortKey: field_name,
          });
      }
      headerDetails.push({ label: "" });
      this.updateState({ headerDetails });
    }
    await this.props.getCustomerPlus(
      `?page=${page}&limit=${limit}&search=${search}`
    );
    let { customerDetails } = this.props;
    this.updateState({
      customerList: customerDetails.dataList,
      originalCustomerList: customerDetails.dataList,
      meta: customerDetails.meta?.pagination,
      isLoading: false,
    });
  };

  handlePagination = (page) => {
    this.getCustomerPlus(page);
  };

  handlesearch = (event) => {
    this.state.search = event.target.value;
    this.getCustomerPlus({});
  };

  bookAppointment = async (data) => {
    let formFields = {};
    formFields["custId"] = data.id;
    formFields["custName"] = data.cust_name;
    await this.props.updateForm("basicApptDetail", formFields);
    this.props.history.push(`/admin/appointment`);
  };

  render() {
    let {
      headerDetails,
      originalCustomerList,
      customerList,
      meta,
      currentIndex,
      isLoading,
    } = this.state;
    let { t } = this.props;
    return (
      <div className="customer-list container-fluid">
        <div className="row mb-4">
          <div className="col-md-4 col-lg-2 mb-2">
            <Link to="/admin/customerplus/settings">
              <NormalButton
                normal={true}
                className="col-12 fs-15 float-right"
                label="Settings"
              />
            </Link>
          </div>
          <div className="col-md-4 col-lg-3 mb-2">
            <Link to="/admin/customerplus/lpmanagement">
              <NormalButton
                normal={true}
                className="col-12 fs-15 float-right"
                label="LP Management"
              />
            </Link>
          </div>
        </div>
        <div className="row">
          <div className="col">
            <div className="row">
              <div className="col-12 mb-2">
                <h3 className="head-label">{t("Customer Plus List")}</h3>
              </div>
              <div className="col-md-8 col-sm-12 mb-2">
                <InputSearch
                  className=""
                  placeholder="Search Customer"
                  onEnter={this.handlesearch}
                  disabled={isLoading}
                />
              </div>
              <div className="col-md-4 col-sm-12">
                <NormalButton
                  mainbg={true}
                  className="col-12 fs-15 float-right"
                  label="Add Customer"
                  onClick={() =>
                    this.props.history.push("/admin/customerplus/add")
                  }
                />
              </div>
            </div>
            <div className="tab-table-content">
              <div className="py-4">
                <div className="table-container">
                  <TableWrapper
                    headerDetails={headerDetails}
                    queryHandler={this.handlePagination}
                    pageMeta={meta}
                    showFilterColumn={true}
                    parentHeaderChange={(value) =>
                      this.updateState(() => (headerDetails = value))
                    }
                    sortData={originalCustomerList}
                    onSort={(list) => this.updateState({ customerList: list })}
                  >
                    {isLoading ? (
                      <tr>
                        <td colSpan="7">
                          <div class="d-flex mt-5 align-items-center justify-content-center">
                            <div class="spinner-border" role="status">
                              <span class="sr-only">Loading...</span>
                            </div>
                          </div>
                        </td>
                      </tr>
                    ) : customerList ? (
                      customerList.map((item, index) => {
                        let { id } = item;
                        return (
                          <tr key={index}>
                            {headerDetails.map((e) =>
                              e.field ? (
                                <td>
                                  <div className="d-flex align-items-center justify-content-center">
                                    {item[e.field]}
                                  </div>
                                </td>
                              ) : null
                            )}
                            <td
                              className="position-relative"
                              ref={(node) => {
                                this.node = node;
                              }}
                              onClick={() => this.handleClick(index)}
                            >
                              {currentIndex === index ? (
                                <>
