import React, { Component } from "react";
import { Nav, NavItem, NavLink, TabContent, TabPane } from "reactstrap";
import classnames from "classnames";
import { InventoryList } from "./InventoryList";
import { withTranslation } from "react-i18next";
import { ItemUsageList } from "../ItemUsageList";

export class InventoryMainPageClass extends Component {
  state = {
    activeMenu: "stockUsageMemo",
  };
  toggle = tab => {
    if (this.state.activeMenu !== tab) {
      this.setState({
        activeMenu: tab,
      });
    }
  };
  render() {
    let { activeMenu } = this.state;
    let { t } = this.props;
    return (
      <>
        <div className="col-12">
          <div className="row">
            <div className="beautesoft-navlink customer-detail mt-3">
              <div className="filled-tabs">
                <div className="tabs-block">
                  <Nav tabs>
                    <NavItem>
                      <NavLink
                        className={classnames({
                          active: this.state.activeMenu === "stockUsageMemo",
                        })}
                        onClick={() => {
                          this.toggle("stockUsageMemo");
                        }}
                      >
                        {t("Stock Usage Memo")}
                      </NavLink>
                    </NavItem>
                    <NavItem>
                      <NavLink
                        className={classnames({
                          active: this.state.activeMenu === "itemUsageMemo",
                        })}
                        onClick={() => {
                          this.toggle("itemUsageMemo");
                        }}
                      >
                        {t("Treatment Usage")}
                      </NavLink>
                    </NavItem>
                  </Nav>
                </div>
              </div>
            </div>
          </div>
          <TabContent activeTab={this.state.activeMenu}>
            <TabPane tabId="stockUsageMemo">
              {activeMenu === "stockUsageMemo" ? (
                <div>
                  <InventoryList />
                </div>
              ) : null}
            </TabPane>
            <TabPane tabId="itemUsageMemo">
              {activeMenu === "itemUsageMemo" ? (
                <div>
                  <ItemUsageList />
                </div>
              ) : null}
            </TabPane>
          </TabContent>
        </div>
      </>
    );
  }
}

export const InventoryMainPage = withTranslation()(InventoryMainPageClass);
