            itemSupp: this.state.supply_itemsval,
            itemParentcode: null,
            itemColor: null,
            itemSizepack: null,
            itemSize: null,
            itemSeason: null,
            itemFabric: null,
            itemBrand: this.state.brand,
            lstpoUcst: null,
            lstpoNo: null,
            lstpoDate: null,
            itemHavechild: false,
            valueApplytochild: false,
            packageDisc: null,
            havePackageDisc: false,
            picPath: this.state.images,
            itemFoc: false,
            itemUom: this.state.uomcode,
            mixbrand: false,
            serviceretail: null,
            itemRange: this.state.range,
            commissionable: this.state.commissionable,
            trading: false,
            custReplenishDays: this.state.Replenishment,
            custAdvanceDays: this.state.Remind_advance,
            salescomm: this.state.Sales_commission,
            workcomm: this.state.work_commission,
            reminderActive: null,
            disclimit: this.state.disclimit,
            disctypeamount: true,
            autocustdisc: this.state.auto_cust_disc,
            reorderActive: this.state.reoreder_level,
            reorderMinqty: this.state.min_qty,
            serviceExpireActive: false,
            serviceExpireMonth: "",
            treatmentLimitActive: false,
            treatmentLimitCount: "",
            limitserviceFlexionly: false,
            salescommpoints: this.state.sales_point,
            workcommpoints: this.state.work_point,
            itemPriceFloor: this.state.floorprice,
            itemPriceCeiling: this.state.priceceiling,
            voucherValue: this.state.vouchervalue,
            voucherValueIsAmount: this.state.vouchervalid,
            voucherValidPeriod: this.state.validity,
            prepaidValue: this.state.prepaidamountone,
            prepaidSellAmt: this.state.prepaidamount,
            prepaidValidPeriod: this.state.valid,
            membercardnoaccess: this.state.card_noacc,
            rptCode: this.state.rptcode,
            isGst: false,
            accountCode: "",
            isOpenPrepaid: false,
            apptWdMin: null,
            istnc: null,
            serviceCost: false,
            serviceCostPercent: false,
            accountCodeTd: this.state.account_no,
            voucherIsvalidUntilDate: false,
            voucherValidUntilDate: null,
            t1TaxCode: this.state.taxone,
            isHaveTax: this.state.tax,
            t2TaxCode: this.state.taxtwo,
            isAllowFoc: this.state.allow_foc,
            vilidityFromDate: null,
            vilidityToDate: null,
            vilidityFromTime: null,
            vilidityToTime: null,
            equipmentcost: null,
            postatus: null,
            moqqty: null,
            printdesc: null,
            printlineno: null,
            itemClassIdId: this.state.itemClassIdId,
            itemDeptIdId: this.state.itemDeptIdId,
            itemDivIdId: this.state.itemDivIdId,
            itemRangeIdId: this.state.itemRangeIdId,
            itemTypeIdId: this.state.itemTypeIdId,
            printuom: null,
            item_seq: this.state.item_seq,
          };
          await this.props
            .NewStocks(newitem)
            .then((data) => {
              Toast({
                type: "success",
                message: "Successfully Added",
              });
              this.handlefinalinput();
            })
            .catch((e) => console.log(e));
        }
      }
    }
  };
  handlefinalinput = () => {
    this.props.history.push(`/admin/backend`);
  };

  handleitemPriceChange = (event, item, index) => {
    if (Number(event.target.value) > 0) {
      let { uomsde } = this.state;
      uomsde[index]["itemPrice"] = event.target.value;
      this.setState({ uomsde });
      console.log(uomsde);
    }
  };
  handleitemCostChange = (event, item, index) => {
    if (Number(event.target.value) > 0) {
      let { uomsde } = this.state;
      uomsde[index]["itemCost"] = event.target.value;
      this.setState({ uomsde });
      console.log(uomsde);
    }
  };
  handleminMarginChange = (event, item, index) => {
    if (Number(event.target.value) > 0) {
      let { uomsde } = this.state;
      uomsde[index]["minMargin"] = event.target.value;
      this.setState({ uomsde });
      console.log(uomsde);
    }
  };
  render() {
    let {
      stkbalanceDetails,
      LinkcodeDetails,
      StockDetails,
      prepaidamount,
      prepaidprice,
      validvoucherdate,
      UOMoneDetails,
      account_no,
      UOMtwoDetails,
      editid,
      editval,
      ItemoneDetails,
      card_noacc,
      prepaidall,
      min_qty,
      Replenishment,
      Remind_advance,
      salescommissiongroup,
      workcommissiongroup,
      work_point,
      sales_point,
      ItemtwoDetails,
      prepaidDetails,
      isopenlinkedit,
      isopenitemedit,
      pageMeta,
      staffList,
      is_loading,
      item_desc,
      prepaid_inclusive,
      prepaid_exclusive,
      prepaidexclusive,
      option,
      count,
      isoption,
      linklist,
      isgeneral,
      uomsde,
      itemClassIdId,
      itemDeptIdId,
      itemDivIdId,
      itemRangeIdId,
      itemTypeIdId,
      isstk,
      islink,
      isstock,
      isvoucher,
      isaccode,
      prepaidftable,
      packageDetails,
      packagetwoDetails,
      isuom,
      isitem,
      isprepaid,
      stockdivision,
      prepaidinclusive,
      Division,
      prepaidamountone,
      dept,
      brand,
      stockname,
      stocktype,
      istaxcode,
      stockprice,
      floorprice,
      disclimit,
      stockclass,
      stock_type,
      range,
      description,
      duration,
      range_desc,
      range_brand,
      range_code,
      range_active,
      reoreder_level,
      iscommission,
      subitemusage,
      customer_replan,
      priceceiling,
      sitecode,
      uomcode,
      isOpendepartment,
      isOpenbrand,
      isOpenclass,
      itemusage_qty,
      itemusage_des,
      isopenrange,
      itemusage_uom,
      images,
      itemusage_code,
      isOpenuom,
      itemusage,
      isOpenlink,
      vouchervalue,
      validity,
      Uoms,
      sitegroup,
      stocklist,
      rangeoption,
      valid,
      uomprice,
      uomcost,
      taxone,
      taxtwo,
      taxoneop,
      Sales_commission,
      work_commission,
      taxtwoop,
      supply_itemsval,
      uommargin,
      validperiod,
      brandlist,
      supplyitem,
      vouchervalid,
      depts,
      classoption,
      control_no,
      inclusive,
      exclusive,
      Inclusive_type,
      exclusive_type,
      commissionable,
      item_active,
      redeem_item,
      open_prepaid,
      percent,
      allow_foc,
      auto_cust_disc,
      tax,
      start_time,
      from_date,
      to_date,
      Appt_TDT,
      package_dept,
      packagedeptvalue,
      appt,
      package_details,
      to_time,
      package_code,
      package_name,
      package_uom,
      package_price,
      package_qty,
      package_discount,
      content_total,
      disc_amount,
      package_total,
      salon,
      retail,
      package_content,
      disc_method,
      itemlevelactive,
      item_seq,
      filterdata,
      Itemdata,
      search,
      itemusage_barcode,
      itemusage_dept,
      itemusage_div,
      searchone,
      filterdataone,
      markup_price,
    } = this.state;

    let { t } = this.props;
    return (
      <>
        <div className="col-md-4 d-flex align-items-center">
          <div className="detail d-flex">
            <p
              className="category"
              onClick={() => this.props.history.push("/admin/backend")}
            >
              {t("Master Data")}
            </p>
            <i className="icon-right mx-md-3"></i>
            <p
              className="sub-category"
              onClick={() => this.props.history.push("/admin/backend")}
            >
              {t("Item Master")}
            </p>
            <i className="icon-right mx-md-3"></i>
            <p className="sub-category">{t("Stock Item Data Entry")}</p>
          </div>
        </div>
        <div className="container-fluid dataentry">
          <div
            className="d-flex  justify-content-between p-3 General "
            onClick={() => this.generalcontent()}
          >
            <p>{t("General")}</p>
            <div className="icon">
              {isgeneral == false ? <AiOutlinePlus /> : <AiOutlineMinus />}
            </div>
          </div>
          {isgeneral == true ? (
            <div className="container-fluid generalcontent">
              <div className="row">
                <div className="col-6 ">
                  <div className="mt-3">
                    <span>{t("Stock Division")}</span>
                    <span className="star">*</span>
                    <div className="input-group">
                      <NormalSelect
                        options={Division}
                        value={stockdivision}
                        onChange={this.temp}
                        name="stockdivision"
                      />
                    </div>
                  </div>
                  <div className="mt-3">
                    <span>{t("Department")}</span>
                    <span className="star">*</span>
                    <div className="d-flex">
                      <div className="input-group">
                        <NormalSelect
                          options={depts}
                          value={dept}
                          name="dept"
                          onChange={this.temp}
                        />
                      </div>
                      <div className="col-3 ">
                        <NormalButton
                          mainbg={true}
                          label={"Add New"}
                          onClick={() => this.handleDialog()}
                        />
                      </div>
                    </div>
                  </div>
                  <div className="mt-3">
                    <span>{t("Brand")}</span>
                    <span className="star">*</span>
                    <div className="d-flex">
                      <div className="input-group">
                        <NormalSelect
                          options={brandlist}
                          value={brand}
                          onChange={this.temp}
                          name="brand"
                        />
                      </div>
                      <div className="col-3 ">
                        <NormalButton
                          mainbg={true}
                          label={"Add New"}
                          onClick={() => this.handlebrandDialog()}
                        />
                      </div>
                    </div>
                  </div>
                  <div className="mt-3 ">
                    <p>{t("Picture")}</p>
                    <div className="mt-3 ml-2">
                      <input
                        type="file"
                        onChange={(event) => this.handleimageChange(event)}
                      />
                    </div>
                  </div>
                  <div className="mt-3">
                    <span>{"Stock Name"}</span>
                    <span className="star">*</span>
                    <div className="input-group">
                      <NormalInput
                        onChange={this.temp}
                        value={stockname}
                        name="stockname"
                        placeholder="Enter Stock name"
                      />
                    </div>
                  </div>
                  <div className="mt-3">
                    <span>{t("Stock Type")}</span>
                    <span className="star">*</span>
                    <div className="input-group">
                      <NormalSelect
                        options={stock_type}
                        value={stocktype}
                        onChange={this.temp}
                        name="stocktype"
                      />
                    </div>
                  </div>
                  <div className="d-flex mt-3">
                    {stockdivision == 3 || stockdivision == 5 ? (
                      <div className="input-group">
                        <p>{t("Price")}</p>
                        <div className="input-group">
                          <NormalInput
                            onChange={this.temp}
                            value={stockprice}
                            name="stockprice"
                            placeholder="Enter price"
                          />
                        </div>
                      </div>
                    ) : (
                      ""
                    )}
                    {stockdivision == 3 ? (
                      <>
                        <div className="col-2"></div>
                        <div className="input-group">
                          <p>{t("Floor Price")}</p>
                          <div className="input-group">
                            <NormalInput
                              onChange={this.temp}
                              value={floorprice}
                              name="floorprice"
                              placeholder="Enter Floor price"
                            />
                          </div>
                        </div>
                      </>
                    ) : (
                      ""
                    )}
                  </div>
                  {stockdivision == 3 ||
                  stockdivision == 4 ||
                  stockdivision == 5 ? (
                    ""
                  ) : (
                    <div className="mt-3">
                      <span>{t("Supplier Code")}</span>
                      <span className="star">*</span>
                      <div className="input-group">
                        <NormalSelect
                          options={supplyitem}
                          value={supply_itemsval}
                          onChange={this.temp}
                          name="supply_itemsval"
                        />
                      </div>
                    </div>
                  )}
                  {stockdivision == 4 || stockdivision == 5 ? (
                    ""
                  ) : (
                    <div className=" d-flex mt-3">
                      <div className="input-group">
                        <p>{"Discount Limit"}</p>
                        <div className="input-group">
                          <NormalInput
                            onChange={this.temp}
                            value={disclimit}
                            name="disclimit"
                            type="number"
                            placeholder="Enter Discount limit"
                          />
                        </div>
                      </div>
                      <div className="col-2"></div>
                      <div className="input-group">
                        <p>{t("Max. Markup price (%) ")}</p>
                        <div className="input-group">
                          <NormalInput
                            onChange={this.temp}
                            value={markup_price}
                            name="markup_price"
                            placeholder="Enter percentage"
                          />
                        </div>
                      </div>
                    </div>
                  )}
                  <div className="mt-3 d-flex">
                    <div className="col-4 d-flex">
                      <NormalCheckbox
                        checked={item_active}
                        name="item_active"
                        onChange={this.handlecheckbox}
                      />
                      <p>{t("Active")}</p>
                    </div>
                    <div className="col-4 d-flex">
                      <NormalCheckbox
                        checked={commissionable}
                        name="commissionable"
                        onChange={this.handlecheckbox}
                      />
                      <p>{t("Commissionable")}</p>
                    </div>
                    <div className="col-4 d-flex">
                      <NormalCheckbox
                        checked={redeem_item}
                        name="redeem_item"
                        onChange={this.handlecheckbox}
                      />
                      <p>{t("Redeem Item")}</p>
                    </div>
                  </div>
                </div>

                <div className="col-6 ">
                  <div className="col-6  mt-5">
                    <div className="input-group">
                      <NormalInput
                        value={control_no}
                        name="control"
                        onChange={this.temp}
                        disabled={true}
                      />
                    </div>
                  </div>
                  <div className="mt-3">
                    <span>{t("Class")}</span>
                    <span className="star">*</span>
                    <div className="d-flex">
                      <div className="input-group">
                        <NormalSelect
                          options={classoption}
                          value={stockclass}
                          onChange={this.temp}
                          name="stockclass"
                        />
                      </div>
                      <div className="col-3 ">
                        <NormalButton
                          mainbg={true}
                          label={"Add New"}
                          onClick={() => this.handleclassDialog()}
                        />
                      </div>
                    </div>
                  </div>
                  <div className="mt-3">
                    <span>{t("Range")}</span>
                    <span className="star">*</span>
                    <div className="d-flex">
                      <div className="input-group">
                        <NormalSelect
                          options={rangeoption}
                          value={range}
                          onChange={this.temp}
                          name="range"
                        />
                      </div>
                      <div className="col-3 ">
                        <NormalButton
                          mainbg={true}
                          label={"Add New"}
                          onClick={() => this.handlerangeDialog()}
                        />
                      </div>
                    </div>
                  </div>
                  <div className="mt-3 imagecontainer">
                    <img src={images} alt="" />
                  </div>
                  <div className="mt-3">
                    <span>{t("Description")}</span>
                    <span className="star">*</span>
                    <div className="input-group">
                      <NormalInput
                        onChange={this.temp}
                        value={item_desc}
                        name="item_desc"
                        placeholder="Enter Description"
                      />
                    </div>
                  </div>
                  {stockdivision == 3 ||
                  stockdivision == 4 ||
                  stockdivision == 5 ? (
                    <div className="mt-3">
                      <span>{t("Duration (Minutes)")}</span>
                      <div className="input-group">
                        <NormalInput
                          value={duration}
                          onChange={this.temp}
                          name="duration"
                          type="number"
                          placeholder="Enter Duration"
                        />
                      </div>
                    </div>
                  ) : null}
                  {stockdivision == 3 ? (
                    <div className="d-flex mt-3">
                      <div className="input-group">
                        <p>{t("Cost")}</p>
                        <div className="input-group">
                          <NormalInput
                            onChange={this.temp}
                            value={priceceiling}
                            name="cost"
                            placeholder="Enter cost"
                          />
                        </div>
                      </div>

                      <div className="col-5">
                        <p>{t("Check")}</p>
                        <div className="d-flex mt-2">
                          <NormalCheckbox />
                          <p>{t("Percent")}</p>
                        </div>
                      </div>
                    </div>
                  ) : (
                    ""
                  )}
                  {stockdivision == 4 || stockdivision == 5 ? (
                    ""
                  ) : (
                    <div className="mt-3">
                      <span>{t("Check")}</span>
                      <div className="d-flex">
                        <div className="col-3">
                          <div className="d-flex">
                            <NormalCheckbox
                              checked={percent}
                              name="percent"
                              onChange={this.handlecheckbox}
                            />
                            <p>{t("Percent")}</p>
                          </div>
                        </div>
                        <div className="col-5">
                          <div className="d-flex">
                            <NormalCheckbox
                              checked={auto_cust_disc}
                              name="auto_cust_disc"
                              onChange={this.handlecheckbox}
                            />
                            <p>{t("Auto Cust Disc")}</p>
                          </div>
                        </div>
                      </div>
                    </div>
                  )}
                  {stockdivision == 5 ? (
                    <div className="mt-3">
                      <span>{t("Check")}</span>
                      <div className="d-flex">
                        <div className="col-3">
                          <div className="d-flex">
                            <NormalCheckbox
                              checked={open_prepaid}
                              name="open_prepaid"
                              onChange={this.handlecheckbox}
                            />
                            <p>{t("Open Prepaid")}</p>
                          </div>
                        </div>
                      </div>
                    </div>
                  ) : (
                    ""
                  )}
                  <div className="mt-3 d-flex">
                    <div className="col-4 d-flex">
                      <NormalCheckbox
                        checked={tax}
                        name="tax"
                        onChange={this.handlecheckbox}
                      />
                      <p>{t("Tax")}</p>
                    </div>
                    <div className="col-4 d-flex">
                      <NormalCheckbox
                        checked={allow_foc}
                        name="allow_foc"
                        onChange={this.handlecheckbox}
                      />
                      <p>{t("Item allow FOC")}</p>
                    </div>
                  </div>
                  <div className="mt-3">
                    <span>{t("Display Sequence")}</span>
                    <div className="input-group">
                      <NormalInput
                        onChange={this.temp}
                        value={item_seq}
                        name="item_seq"
                        type="number"
                      />
                    </div>
                  </div>
                </div>
              </div>
            </div>
          ) : (
            ""
          )}

          {commissionable == true ? (
            <div>
              <div
                className="d-flex  justify-content-between p-3 General mt-5"
                onClick={() => this.commissioncontent()}
              >
                <p>{t("commission")}</p>
                <div className="icon">
                  {iscommission == false ? (
                    <AiOutlinePlus />
                  ) : (
                    <AiOutlineMinus />
                  )}
                </div>
              </div>
              {iscommission == true ? (
                <div className="container-fluid ">
                  <div className="row">
                    <div className="col-6 ">
                      <div className="mt-3">
                        <span>{t("Sales Commission Group")}</span>
                        <span style={{ color: "red" }}>*</span>
                        <div className="input-group">
                          <NormalSelect
                            options={salescommissiongroup}
                            value={Sales_commission}
                            onChange={this.sublist}
                            name="Sales_commission"
                          />
                        </div>
                      </div>
                      <div className="mt-3">
                        <span>{t("Work Commission Group")}</span>
                        <span style={{ color: "red" }}>*</span>
                        <div className="input-group">
                          <NormalSelect
                            options={workcommissiongroup}
                            value={work_commission}
                            onChange={this.sublist}
                            name="work_commission"
                          />
                        </div>
                      </div>
                    </div>
                    <div className="col-6 ">
                      <div className="mt-3">
                        <span>{t("Sales Points")}</span>
                        <span style={{ color: "red" }}>*</span>
                        <div className="d-flex">
                          <div className="input-group">
                            <NormalInput
                              value={sales_point}
                              onChange={this.sublist}
                              name="sales_point"
                              placeholder="Enter Sales point"
                            />
                          </div>
                        </div>
                      </div>
                      <div className="mt-3">
                        <span>{t("Work Points")}</span>
                        <span style={{ color: "red" }}>*</span>
                        <div className="d-flex">
                          <div className="input-group">
                            <NormalInput
                              value={work_point}
                              onChange={this.sublist}
                              name="work_point"
                              placeholder="Enter work point"
                            />
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              ) : (
                ""
              )}
            </div>
          ) : (
            ""
          )}

          {stocktype == "PACKAGE" ? (
            <div>
              <div
                className="d-flex  justify-content-between p-3 General mt-5"
                onClick={() => this.commissioncontent()}
              >
                <p>{t("PACKAGE")}</p>
                <div className="icon">
                  {iscommission == false ? (
                    <AiOutlinePlus />
                  ) : (
                    <AiOutlineMinus />
                  )}
                </div>
              </div>
              {iscommission == true ? (
                <div className="container-fluid ">
                  <div className="row">
                    <div className="col-8">
                      <div className="d-flex">
                        <div className="mt-3 col-6">
                          <span>{t("From Date")}</span>
                          <div className="input-group">
                            <NormalDate
                              value={from_date}
                              name="from_date"
                              onChange={this.package}
                            />
                          </div>
                        </div>
                        <div className="mt-3 col-6">
                          <span>{t("To Date")}</span>
                          <div className="input-group">
                            <NormalDate
                              value={to_date}
                              name="to_date"
                              onChange={this.package}
                            />
                          </div>
                        </div>
                      </div>
                      <div className="d-flex ">
                        <div className="mt-3 col-6">
                          <span>{t("From Time")}</span>
                          <div className="input-group">
                            <NormalTimePicker
                              className={`cursor-pointer`}
                              onChange={this.fromtimechange}
                              name="start_time"
                              timeOnly={true}
                              dateFormat={`HH:mm`}
                              showTime={true}
                              selected={false}
                              placeholder=""
                              timeIntervals={15}
                              value={start_time}
                              showIcon={false}
                            />
                          </div>
                        </div>
                        <div className="mt-3 col-6">
                          <span>{t("To Time")}</span>
                          <div className="input-group">
                            <NormalTimePicker
                              className={`cursor-pointer`}
                              onChange={this.totimechange}
                              name="to_time"
                              timeOnly={true}
                              dateFormat={`HH:mm`}
                              showTime={true}
                              selected={false}
                              placeholder=""
                              timeIntervals={15}
                              value={to_time}
                              showIcon={false}
                            />
                          </div>
                        </div>
                      </div>
                      <div className="d-flex">
                        <div className=" d-flex mt-3 col-6">
                          <NormalCheckbox
                            checked={Appt_TDT}
                            name="Appt_TDT"
                            onChange={this.package}
                          />
                          <span style={{ color: "red" }}>{t("Appt TDT")}</span>
                        </div>

                        <div className="mt-3 col-6">
                          <span style={{ color: "red" }}>
                            {t("min. select for Appt:")}
                          </span>
                          <div className="input-group">
