          <div
            className="d-flex  justify-content-between p-3 General mt-5"
            onClick={() => this.Accountcodecontent()}
          >
            <p>{t("Account Code")}</p>
            <div className="icon">
              {isaccode == false ? <AiOutlinePlus /> : <AiOutlineMinus />}
            </div>
          </div>
          {isaccode == true ? (
            <div className="container-fluid">
              <div className="d-flex mt-3 justify-content-center">
                <div className="mt-1">
                  <p>{t("Account Code")}</p>
                </div>
                <div className="col-4">
                  <NormalInput
                    placeholder={"Enter account code"}
                    value={account_no}
                    type="number"
                    onChange={this.temp}
                  />
                </div>
              </div>
            </div>
          ) : (
            ""
          )}

          {tax == true ? (
            <>
              <div
                className="d-flex  justify-content-between p-3 General mt-5"
                onClick={() => this.taxcodecontent()}
              >
                <p>{t("Tax Code")}</p>
                <div className="icon">
                  {istaxcode == false ? <AiOutlinePlus /> : <AiOutlineMinus />}
                </div>
              </div>
              {istaxcode == true ? (
                <div className="container-fluid ">
                  <div className="row">
                    <div className="col-6 ">
                      <div className="mt-3">
                        <span>{t("1st Tax Code")}</span>
                        <span className="star">*</span>
                        <div className="input-group">
                          <NormalSelect
                            options={taxoneop}
                            value={taxone}
                            onChange={this.temp}
                            name="taxone"
                          />
                        </div>
                      </div>
                    </div>
                    <div className="col-6 ">
                      <div className="mt-3">
                        <span>{t("2nd Tax Code")}</span>
                        <span className="star">*</span>
                        <div className="d-flex">
                          <div className="input-group">
                            <NormalSelect
                              options={taxtwoop}
                              value={taxtwo}
                              onChange={this.temp}
                              name="taxtwo"
                            />
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              ) : (
                ""
              )}
            </>
          ) : (
            ""
          )}
        </div>

        <div className="mt-5 col-1">
          <NormalButton
            mainbg={true}
            label={"Add"}
            onClick={() => this.Additem()}
          />
        </div>
        <NormalModal
          className={"payment-modal"}
          style={{ minWidth: "50%" }}
          modal={isOpendepartment}
          handleModal={this.handleDialog}
        >
          <img
            onClick={this.handleDialog}
            className="close cursor-pointer"
            src={closeIcon}
            alt=""
          />

          <Departmentpopup
            handleModal={this.handleDialog}
            handlelist={this.listofdept}
          ></Departmentpopup>
        </NormalModal>
        <NormalModal
          className={"payment-modal"}
          style={{ minWidth: "45%" }}
          modal={isOpenbrand}
          handleModal={this.handlebrandDialog}
        >
          <img
            onClick={this.handlebrandDialog}
            className="close cursor-pointer"
            src={closeIcon}
            alt=""
          />

          <Brandpopup
            handleModal={this.handlebrandDialog}
            handlelist={this.listofbrand}
          ></Brandpopup>
        </NormalModal>
        <NormalModal
          className={"payment-modal"}
          style={{ minWidth: "60%" }}
          modal={isOpenclass}
          handleModal={this.handleclassDialog}
        >
          <img
            onClick={this.handleclassDialog}
            className="close cursor-pointer"
            src={closeIcon}
            alt=""
          />

          <Classpopup
            handleModal={this.handleclassDialog}
            handlelist={this.listofclasses}
          ></Classpopup>
        </NormalModal>
        <NormalModal
          className={"payment-modal"}
          style={{ minWidth: "60%" }}
          modal={isOpenuom}
          handleModal={this.handleuomDialog}
        >
          <img
            onClick={this.handleuomDialog}
            className="close cursor-pointer"
            src={closeIcon}
            alt=""
          />

          <Newuompopup
            handleModal={this.handleuomDialog}
            listofuom={this.listofuomprices}
          ></Newuompopup>
        </NormalModal>
        <NormalModal
          className={"payment-modal"}
          style={{ minWidth: "40%" }}
          modal={isOpenlink}
          handleModal={this.handlelinkDialog}
        >
          <img
            onClick={this.handlelinkDialog}
            className="close cursor-pointer"
            src={closeIcon}
            alt=""
          />

          <Newlinkpopup
            handleModal={this.handlelinkDialog}
            name={item_desc}
            listoflinklist={this.listoflinklist}
          ></Newlinkpopup>
        </NormalModal>
        <NormalModal
          className={"payment-modal"}
          style={{ minWidth: "40%" }}
          modal={isopenlinkedit}
          handleModal={this.handleEditlinkDialog}
        >
          <img
            onClick={this.handleEditlinkDialog}
            className="close cursor-pointer"
            src={closeIcon}
            alt=""
          />

          <Editlinkpopup
            handleModal={this.handleEditlinkDialog}
            name={editval}
            code={editid}
            listoflinklist={this.listofEditlinklist}
          ></Editlinkpopup>
        </NormalModal>

        <NormalModal
          className={"payment-modal"}
          style={{ minWidth: "40%" }}
          modal={isopenitemedit}
          handleModal={this.handleEdititemDialog}
        >
          <img
            onClick={this.handleEdititemDialog}
            className="close cursor-pointer"
            src={closeIcon}
            alt=""
          />
          <div>
            <h6>Add</h6>
            <div className="mt-4 d-flex">
              <div className="col-6">
                <p>Item Code</p>
                <NormalInput
                  value={itemusage_code}
                  name="item_code"
                  onChange={this.Item_Code}
                  disabled={true}
                />
              </div>
              <div className="col-6">
                <span>Item Description</span>
                <span style={{ color: "red" }}>*</span>
                <NormalInput
                  value={itemusage_des}
                  name="itemusage_des"
                  onChange={this.Item_Code}
                  disabled={true}
                />
              </div>
            </div>
            <div className="mt-4 d-flex">
              <div className="col-6">
                <p>UOM</p>
                <NormalSelect
                  value={itemusage_uom}
                  options={Uoms}
                  name="itemusage_uom"
                  onChange={this.Item_Code}
                  disabled={true}
                />
              </div>
              <div className="col-6">
                <span>Qty</span>
                <span style={{ color: "red" }}>*</span>
                <NormalInput
                  value={itemusage_qty}
                  name="itemusage_qty"
                  onChange={this.Item_Code}
                />
              </div>
            </div>
            <div className="d-flex mt-3 justify-content-between">
              <div className="pl-2">
                <NormalButton
                  mainbg={true}
                  label={"Cancel"}
                  onClick={this.handleEdititemDialog}
                />
              </div>
              <div className="pr-2">
                <NormalButton
                  mainbg={true}
                  label={"Submit"}
                  onClick={() =>
                    this.Additemusagetable(
                      itemusage_code,
                      itemusage_des,
                      itemusage_barcode,
                      itemusage_dept,
                      itemusage_div,
                      itemusage_uom,
                      itemusage_qty,
                      itemlevelactive
                    )
                  }
                />
              </div>
            </div>
          </div>
        </NormalModal>

        <NormalModal
          className={"payment-modal"}
          style={{ minWidth: "50%" }}
          modal={isopenrange}
          handleModal={this.handlerangeDialog}
        >
          <img
            onClick={this.handlerangeDialog}
            className="close cursor-pointer"
            src={closeIcon}
            alt=""
          />
          <div>
            <h6>Add New Range</h6>
            <div className="mt-4 d-flex">
              <div className="col-6">
                <span>Code</span>
                <span style={{ color: "red" }}>*</span>
                <NormalInput
                  value={range_code}
                  name="range_code"
                  type="number"
                  disabled={true}
                  onChange={this.handlerange}
                />
              </div>
              <div className="col-6">
                <span>Description</span>
                <span style={{ color: "red" }}>*</span>
                <NormalInput
                  value={range_desc}
                  name="range_desc"
                  onChange={this.handlerange}
                />
              </div>
            </div>
            <div className="mt-4 d-flex">
              <div className="col-6">
                <span>Brand</span>
                <span style={{ color: "red" }}>*</span>
                <NormalSelect
                  value={range_brand}
                  name="range_brand"
                  options={brandlist}
                  onChange={this.handlerange}
                />
              </div>
              <div className="col-6 d-flex mt-3">
                <NormalCheckbox
                  checked={range_active}
                  name="range_active"
                  onChange={this.handlerange}
                />
                <p>{t("Active")}</p>
              </div>
            </div>
            <div className="d-flex mt-3 justify-content-between">
              <div className="pl-2">
                <NormalButton
                  mainbg={true}
                  label={"Cancel"}
                  onClick={this.handlerangeDialog}
                />
              </div>
              <div className="pr-2">
                <NormalButton
                  mainbg={true}
                  label={"Submit"}
                  onClick={() =>
                    this.Addrangeitems(
                      range_code,
                      range_desc,
                      range_brand,
                      range_active
                    )
                  }
                />
              </div>
            </div>
          </div>
        </NormalModal>
      </>
    );
  }
}

const mapDispatchToProps = (dispatch) => {
  return bindActionCreators(
    {
      ItemDivs,
      ItemUom,
      ItemSitelists,
      VoucherConditions,
      ItemBrands,
      ItemSupplies,
      ItemDepts,
      ItemLinks,
      ItemUomprices,
      ItemTypes,
      ItemClasses,
      NewItemStocklists,
      ItemRanges,
      getStocks,
      NewItemRanges,
      NewStocks,
      createStaffPlus,
      CommGroupHdrs,
      TaxType2TaxCodes,
      PrepaidOpenConditions,
      TaxType1TaxCodes,
      NewItemLinks,
      NewPrepaidOpenConditions,
      ItemBatches,
      NewPackageDtls,
      NewItemUomprices,
      PackageDtls,
      PackageItemDetails,
      NewPackageHdrs,
      NewUsagelevels,
    },
    dispatch
  );
};

export const DataEntry = withTranslation()(
  connect(null, mapDispatchToProps)(DataEntryClass)
);
