import React, { Component } from "react";
import "./style.scss";
import { InputSearch,NormalDateTime   } from "component/common";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { history } from "helpers";
import { getCommonApi, commonCreateApi } from "redux/actions/common";
import Woman from "assets/images/woman.png";
import Box from "assets/images/box.png";
import Coupon from "assets/images/coupon.png";
import Like from "assets/images/like.png";
import Rise from "assets/images/rise.png";
import TotalCollection from "assets/images/TotalCollection.png";
import TreatmentDone from "assets/images/TreatmentDone.png";
import { dateFormat } from "service/helperFunctions";
import { Toast } from "service/toast";

export class NewQuickStatsClass extends Component {
  state = {
    customer: { daily_custcnt: 0, monthly_custcnt: 0, total_cust: 0 },
    product_sold: {
      dailyproduct_qty: 0,
      monthlyproduct_qty: 0,
      daily_product: "0.00",
      monthly_product: "0.00",
      daily_product_ar: "0.00",
      monthly_product_ar: "0.00",
    },
    service_sold: {
      dailyservice_qty: 0,
      monthlyservice_qty: 0,
      daily_service: "0.00",
      monthly_service: "0.00",
      daily_service_ar: "0.00",
      monthly_service_ar: "0.00",
    },
    voucher_sold: {
      dailyvoucher_qty: 0,
      monthlyvoucher_qty: 0,
      daily_voucher: "0.00",
      monthly_voucher: "0.00",
    },
    prepaid_sold: {
      dailyprepaid_qty: 0,
      monthlyprepaid_qty: 0,
      daily_prepaid: "0.00",
      monthly_prepaid: "0.00",
      daily_prepaid_ar: "0.00",
      monthly_prepaid_ar: "0.00",
    },
    treatment_done: {
      daily_tdqty: 0,
      monthly_tdqty: 0,
      daily_tdamt: "0.00",
      monthly_tdamt: "0.00",
    },
    total_collection: {
      daily_sales: "0.00",
      monthly_sales: "0.00",
      daily_nonsales: "0.00",
      monthly_nonsales: "0.00",
      total_daily: "0.00",
      total_monthly: "0.00",
    },
    formField: {
      fromDate:  new Date(new Date().getFullYear(), new Date().getMonth(), 1),
      toDate: new Date(new Date().getFullYear(), new Date().getMonth() + 1, 0),
    },
  };

  componentDidMount() {
    this.getCustomerProductandService();
    this.getVoucherandPrepaid();
    this.getTreatmentandTotal();
  }

  getCustomerProductandService = () => {
    let { customer, product_sold, service_sold,formField } = this.state;
    let { fromDate, toDate} = formField;
    let From = new Date();
    console.log(fromDate)
    if (fromDate && fromDate !== "") {
       
      From = fromDate;
    } else {
      this.setState({ fromDate: From });
    }
    let To = new Date();
    if (toDate && toDate !== "") {
      To = toDate;
    } else {
      this.setState({ toDate: To });
    }
    console.log(From)
    this.props.getCommonApi(`dashboardcust/?from_date=${dateFormat(
      From,
      "yyyy-mm-dd"
    )}&to_date=${dateFormat(
      To,
      "yyyy-mm-dd"
    )}`).then(res => {
      console.log(res, "custdashbordpart");
      customer = res.customer;
      product_sold = res.product_sold;
      service_sold = res.service_sold;
      this.setState({
        customer,
        product_sold,
        service_sold,
      });
    });
    
    console.log(customer)
  };
  getVoucherandPrepaid = () => {
    let { voucher_sold, prepaid_sold,formField } = this.state;
    let { fromDate, toDate} = formField;
    let From = new Date();
    if (fromDate && fromDate !== "") {
      From = fromDate;
    } else {
      this.setState({ fromDate: From });
    }
    let To = new Date();
    if (toDate && toDate !== "") {
      To = toDate;
    } else {
      this.setState({ toDate: To });
    }
    this.props.getCommonApi(`dashboardvoucher/?from_date=${dateFormat(
      From,
      "yyyy-mm-dd"
    )}&to_date=${dateFormat(
      To,
      "yyyy-mm-dd"
    )}`).then(res => {
      console.log(res, "voucherandprepaid");
      voucher_sold = res.voucher_sold;
      prepaid_sold = res.prepaid_sold;
      this.setState({
        voucher_sold,
        prepaid_sold,
      });
    });
  };
  getTreatmentandTotal = () => {
    let { treatment_done, total_collection,formField } = this.state;
    let { fromDate, toDate} = formField;
    let From = new Date();
    if (fromDate && fromDate !== "") {
      From = fromDate;
    } else {
      this.setState({ fromDate: From });
    }
    let To = new Date();
    if (toDate && toDate !== "") {
      To = toDate;
    } else {
      this.setState({ toDate: To });
    }
    this.props.getCommonApi(`dashboardtd/?from_date=${dateFormat(
      From,
      "yyyy-mm-dd"
    )}&to_date=${dateFormat(
      To,
      "yyyy-mm-dd"
    )}`).then(res => {
      console.log(res, "treatment and total");
      treatment_done = res.treatment_done;
      total_collection = res.total_collection;
      this.setState({
        treatment_done,
        total_collection,
      });
    });
  };

  handleDatePick = async (name, value) => {
    console.log(name, value, "sdfgdfhfshg", dateFormat(value));
    // dateFormat(new Date())
    let { formField } = this.state;
    formField[name] = value;
    // formFields[name] = value;
    if(this. checktime())
    {
    await this.setState({
      formField,
    });
    // this.getCart()
    this.getCustomerProductandService();
    this.getVoucherandPrepaid();
    this.getTreatmentandTotal();
  }
  };
  checktime = () =>
  {
    let { formField } = this.state;
    let { fromDate, toDate} = formField;
    if(fromDate > toDate)
    {
      Toast({
        type: "info",
        message: "From date should not more than todate",
      });
      return false;
    }
    else
    {
      return true;
    }
  }
  render() {
    let {
      customer,
      product_sold,
      service_sold,
      voucher_sold,
      prepaid_sold,
      treatment_done,
      total_collection,
      formField,

    } = this.state;
    let { tokenDetail } = this.props;
    let { fromDate, toDate} = formField;
    return (
      <div className="quickStats">
        <div className="palette ">
            <div className="color-detail col-md-7">
              <div className="color"></div>
              <div className="detail">Daily value</div>
            </div>
            <div className="color-detail col-md-7">
              <div className="color not-available"></div>
              <div className="detail">Monthly value</div>
            </div>
            <div className = "last col-md-12">
            <div className="detail">From</div>
                <div className="input-group date">
                <NormalDateTime
                    onChange={this.handleDatePick}
