                handleModal={this.handleDialog}
              ></CourseTreatmentDone>
            </NormalModal>
          </div>
        </div>
      </>
    );
  }
}

const mapStateToProps = state => ({});

const mapDispatchToProps = dispatch => {
  return bindActionCreators(
    {
      getCommonApi,
      commonPatchApi,
      commonCreateApi,
      commonUpdateApi,
    },
    dispatch
  );
};
export const CoursePopup = connect(
  mapStateToProps,
  mapDispatchToProps
)(CoursePopupClass);
