import React, { Component } from "react";
import "./style.scss";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { getCommonApi, commonCreateApi } from "redux/actions/common";
import user from "assets/images/user-image.png";
import { getTokenDetails } from "redux/actions/auth";
import { withTranslation } from "react-i18next";
import { SetupTransCreateClass } from "component/Admin/Settings/SetupTransaction/SetupTransCreate";
import { AiOutlinePlus, AiOutlineMinus } from "react-icons/ai";

export class NewStaffStatsClass extends Component {
  state = { staffList: [], limit: 8, page: 1, meta: {}, isAttendance: true };
  componentDidMount = () => {
    this.getStafflist({});
  };
  getStafflist = data => {
    let { page, limit } = this.state;
    this.props
      .getCommonApi(`attendancestaff/?page=${page}&limit=${limit}`)
      .then(async key => {
        let { status, data } = key;
        if (status === 200) {
          console.log(key, "sdfgsdfgsdfgdfg sdfgsdfgsdfg");
          let { staffList } = this.state;
          let { meta } = this.state;
          staffList = [];
          meta = {};
          await this.setState({
            staffList,
            meta,
          });
          if (data.dataList && data.dataList.length > 0) {
            staffList = data.dataList;
            meta = data.meta.pagination;
            console.log(meta, "metalist");
            await this.setState({
              staffList,
              meta,
            });
          }
        }
      });
  };

  handleNext = async () => {
    let { page } = this.state;
    page = page + 1;
    await this.setState({
      page,
    });
    if (page > 0) {
      this.getStafflist();
    }
  };

  handleBack = async () => {
    let { page } = this.state;
    page = page - 1;
    await this.setState({
      page,
    });
    if (page > 0) {
      this.getStafflist();
    }
  };

  handleFlag = tab => {
    this.setState(prevState => ({
      [tab]: !prevState[tab],
    }));
  };
  render() {
    let { staffList, meta, isAttendance } = this.state;
    let { tokenDetail, t } = this.props;
    return (
      <div className="staffStats pb-1">
        <div className="row pb-4">
          <div className="color-title col-md-7 col-12 fs-18 fw-500">
            {t("Hello")} {tokenDetail.username},
          </div>
        </div>
        <div
          className="col-md-12 col-12 Accord-header cursor-pointer"
          onClick={() => this.handleFlag("isAttendance")}
        >
          <div className="d-flex">
            <div className="h5 fw-500 col-10 col-md-10 text-left">
              {`Staffs in Attendance Today`}
            </div>
            <div className="h5 fw-500 col-2 col-md-2 text-right fs-18">
              {isAttendance == false ? <AiOutlinePlus /> : <AiOutlineMinus />}
            </div>
          </div>
        </div>
        {isAttendance && (
          <div className="d-flex justify-content-start mt-3">
            {staffList && staffList.length > 0 ? (
              <div
                className="d-flex justify-content-start align-items-center forward-button cursor-pointer fw-700 mr-5"
                onClick={this.handleBack}
              >
                {meta && Number(meta.current_page) !== 1 ? (
                  <svg
                    width="10"
                    height="25"
                    viewBox="0 0 6 10"
                    fill="none"
                    xmlns="http://www.w3.org/2000/svg"
                  >
                    <path
                      d="M5 0.5L1 5L5 9.5"
                      stroke="#000000"
                      strokeLinecap="round"
                      strokeLinejoin="round"
                    />
                  </svg>
                ) : null}
              </div>
            ) : null}
            <div className="row">
              {staffList && staffList.length > 0 ? (
                staffList.map((item, index) => {
                  return (
                    <div className={`d-flex product-card card`} key={index}>
                      <div className="d-flex justify-content-center">
                        <div className="cart-img">
                          <img src={item.enable ? item.emp_img : null} alt="" />
                        </div>
                      </div>
                      <div className="d-flex flex-wrap justify-content-center pt-1">
                        <p className="customer-label">{item.emp_name}</p>
                      </div>
                    </div>
                  );
                })
              ) : (
                <div className="mx-4 staff-list cursor-pointer">
                  {t("No staffs in attendance")}
                </div>
              )}
            </div>
            {staffList && staffList.length > 0 ? (
              <div
                className="d-flex justify-content-end align-items-center back-button cursor-pointer ml-5 fw-700"
                onClick={this.handleNext}
              >
                {meta &&
                Number(meta.current_page) !== Number(meta.total_pages) ? (
                  <svg
                    width="10"
                    height="25"
                    viewBox="0 0 6 10"
                    fill="none"
                    xmlns="http://www.w3.org/2000/svg"
                  >
                    <path
                      d="M0.5 9.5L4.5 5L0.5 0.5"
                      stroke="#000000"
                      strokeLinecap="round"
                      strokeLinejoin="round"
                    />
                  </svg>
                ) : null}
              </div>
            ) : null}
          </div>
        )}
      </div>
    );
  }
}

const mapStateToProps = state => ({
  tokenDetail: state.authStore.tokenDetails,
});
const mapDispatchToProps = dispatch => {
  return bindActionCreators(
    {
      getCommonApi,
      commonCreateApi,
      getTokenDetails,
    },
    dispatch
  );
};

export const NewStaffStats = withTranslation()(
  connect(mapStateToProps, mapDispatchToProps)(NewStaffStatsClass)
);
