ement("div",{className:"d-flex align-items-center justify-content-center"},unit_amount)));}):/*#__PURE__*/React.createElement("tr",null,/*#__PURE__*/React.createElement("td",{colSpan:"12"},/*#__PURE__*/React.createElement("div",{className:"d-flex align-items-center justify-content-center"},"No record found")))))),/*#__PURE__*/React.createElement(NormalModal,{className:"transaction-done-modal",style:{minWidth:"60%"},modal:isOpenTreatmentDone,handleModal:function handleModal(){}},/*#__PURE__*/React.createElement("img",{onClick:this.handleDialog,className:"close cursor-pointer",src:closeIcon,alt:""}),/*#__PURE__*/React.createElement(CourseTreatmentDone,{id:cartId,cartId:this.props.cartId,sessionCount:data_list.Sessions,depositAmt:data_list.deposit,handleModal:this.handleDialog})))),isOpenStaffSelectionDetail||!isOpenStaffSelectionDetail?/*#__PURE__*/React.createElement(NormalModal,{className:"transaction-done-modal top-up-modal",style:{minWidth:"75%"},modal:isOpenStaffSelectionDetail,handleModal:function handleModal(){}},/*#__PURE__*/React.createElement("img",{onClick:this.handleDialogSalesStaff,className:"close cursor-pointer",src:closeIcon,alt:""}),/*#__PURE__*/React.createElement(StaffSelectionPopup,{id:this.props.id,cartId:this.props.cartId,LinelevelCartId:this.props.cartId,handleModal:this.handleDialogSalesStaff})):null);}}]);return CoursePopupClass;}(Component);var mapStateToProps=function mapStateToProps(state){return{tokenDetails:state.authStore.tokenDetails};};var mapDispatchToProps=function mapDispatchToProps(dispatch){return bindActionCreators({getCommonApi:getCommonApi,commonPatchApi:commonPatchApi,commonCreateApi:commonCreateApi,commonUpdateApi:commonUpdateApi},dispatch);};export var CoursePopup=withTranslation()(connect(mapStateToProps,mapDispatchToProps)(CoursePopupClass));

