import React, { Component } from "react";
import { NormalButton, NormalSelect } from "component/common";
import { InputSearch, TableWrapper } from "component/common";
import { NormalInput, NormalDateTime } from "component/common";

import "./style.scss";
import { dateFormat } from "service/helperFunctions";
import { history } from "helpers";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { getCommonApi, commonCreateApi } from "redux/actions/common";
import { withTranslation } from "react-i18next";

export class TransactionHistoryClass extends Component {
  state = {
    headerDetails: [
      { label: "Cust Code" },
      { label: "Cust Name" },
      { label: "Paid On" },
      {
        label: "Modified Date",
        className: "d-none",
        divClass: "justify-content-end text-right",
      },
      { label: "Amount", divClass: "justify-content-end text-right" },
      {
        label: "Total Amount",
        className: "d-none",
        divClass: "justify-content-end text-right",
      },
      {
        label: "Paid Amount",
        className: "d-none",
        divClass: "justify-content-end text-right",
      },
      { label: "Item", width: "300px", className: "d-none" },
      { label: "Transaction No" },
      { label: "Reference No" },
      { label: "Billed By" },
      {
        label: "No of Qty",
        className: "d-none",
        divClass: "justify-content-end text-right",
      },
      {
        label: "No of Lines",
        className: "d-none",
        divClass: "justify-content-end text-right",
      },
      {
        label: "OR",
        className: "d-none",
        divClass: "justify-content-end text-right",
      },
      { label: "Status" },
      { label: "Type" },
      { label: "Payment Remark" },
      { label: "" },
    ],
    billingList: [],
    pageMeta: {
      chunk: 10,
      page: 1,
      total: 10,
      totalPages: 2,
    },
    formField: {
      fromDate: new Date(),
      toDate: new Date(),
      transCode: "",
      custCode: "",
      custName: "",
      transtype: "All",
    },
    active: false,
    currentIndex: -1,
    page: 1,
    limit: 10,
    isOpenvoidCheckout: false,
    transtypeOptions: [
      { label: "All", value: "All" },
      { label: "Sales", value: "Sales" },
      { label: "Void", value: "Void" },
      { label: "TD", value: "TD" },
      { label: "Non-Sales", value: "NonSales" },
    ],
    settingData: {},
    TotalTransactionAmount: "",
  };

  componentDidMount() {
    let From = new Date();
    let { formField } = this.state;
    let firstdayMonth = new Date(From.getFullYear(), From.getMonth(), 1);
    formField["fromDate"] = firstdayMonth;
    this.setState({
      formField,
    });

    this.getTransactions();
    this.handleItemSettings();
  }

  handleItemSettings = () => {
    let { settingData } = this.state;
    this.props.getCommonApi(`userlist/`).then(key => {
      let { status, data } = key;
      console.log(key, "settingsData changeprice");
      if (status === 200) {
        settingData = data;
        this.setState({ settingData });
      }
    });
  };
  getTransactions = () => {
    let {
      billingList,
      pageMeta,
      formField,
      page,
      limit,
      TotalTransactionAmount,
    } = this.state;
    let { fromDate, toDate, transCode, custCode, custName, transtype } =
      formField;

    let From = new Date();
    if (fromDate && fromDate !== "") {
      From = fromDate;
    } else {
      this.setState({ fromDate: From });
    }
    let To = new Date();
    if (toDate && toDate !== "") {
      To = toDate;
    } else {
      this.setState({ toDate: To });
    }
    this.props
      .getCommonApi(
        `transactionhistory/?from_date=${dateFormat(
          From,
          "yyyy-mm-dd"
        )}&to_date=${dateFormat(
          To,
          "yyyy-mm-dd"
        )}&transac_no=${transCode}&cust_code=${custCode}&cust_name=${custName}&page=${page}&limit=${limit}&invoice_type=${transtype}`
      )
      .then(async res => {
        console.log(res, "transactionlistdsfdfaafg");
        await this.setState({ billingList: [] });
        billingList = res.data.dataList;
        pageMeta = res.data.meta.pagination;
        TotalTransactionAmount = res.sa_total;
        this.setState({ billingList, pageMeta, TotalTransactionAmount });
      });
  };
  handlePrint = id => {
    let { billingList, pageMeta, formField, page, limit } = this.state;
    let { fromDate, toDate, transCode, custCode, custName } = formField;
    this.props.getCommonApi(`receiptpdf/?sa_transacno=${id}`).then(res => {});
  };

  handlePagination = async pageNo => {
    let { page } = this.state;
    page = pageNo.page;
    await this.setState({
      page,
    });
    this.getTransactions();
  };

  handleShare = id => {
    this.props
      .commonCreateApi(`receiptpdfsend/?sa_transacno=${id}`)
      .then(res => {});
  };

  handleInvoice = id => {
    this.props
      .getCommonApi(`customerreceiptprint/?sa_transacno=${id}`)
      .then(res => {});
  };

  handleClick = key => {
    if (!this.state.active) {
      document.addEventListener("click", this.handleOutsideClick, false);
    } else {
      document.removeEventListener("click", this.handleOutsideClick, false);
    }

    this.setState(prevState => ({
      active: !prevState.active,
      currentIndex: key,
    }));
  };

  handleOutsideClick = e => {
    if (this.node != null) {
      if (this.node.contains(e.target)) {
        return;
      }
    }
    this.handleClick();
  };

  handleChange = async ({ target: { value, name } }) => {
    let { formField } = this.state;
    formField[name] = value;
    await this.setState({
      formField,
    });
  };

  handleDatePick = async (name, value) => {
    let { formField } = this.state;
    formField[name] = value;
    await this.setState({
      formField,
    });
  };

  handleSearch = () => {
    this.getTransactions();
  };

  render() {
    let {
      headerDetails,
      pageMeta,
      billingList,
      formField,
      transtypeOptions,
      settingData,
      TotalTransactionAmount,
    } = this.state;
    let { fromDate, toDate, transCode, custCode, custName, transtype } =
      formField;
    let { t } = this.props;
    return (
      <>
        <div className="billing-section">
          <div className="col-md-12 d-flex align-items-center">
            <p className="label-head mb-4">{t("Transaction History")}</p>
          </div>
          <div className="row m-0 filter">
            <div className="col-md-3 col-12">
              <div className="d-flex mb-2">
                <label className="text-left w-100 text-black common-label-text mr-2">
                  {t("From Date")}
                </label>
                <div className="input-group">
                  <NormalDateTime
                    onChange={this.handleDatePick}
                    inputcol="p-0 inTime"
                    value={fromDate ? new Date(fromDate) : new Date()}
                    name="fromDate"
                    //className="dob-pick"
                    showYearDropdown={true}
                    dateFormat="dd/MM/yyyy"
                    maxDate={new Date(toDate)}
                    showDisabledMonthNavigation
                  />
                </div>
              </div>
              <div className="d-flex mb-2">
                <label className="text-left w-100 text-black common-label-text mr-2">
                  {t("To Date")}
                </label>
                <div className="input-group">
                  <NormalDateTime
                    onChange={this.handleDatePick}
                    inputcol="p-0 inTime"
                    value={toDate ? new Date(toDate) : new Date()}
                    name="toDate"
                    // className="dob-pick"
                    showYearDropdown={true}
                    dateFormat="dd/MM/yyyy"
                    minDate={new Date(fromDate)}
                    showDisabledMonthNavigation
                  />
                </div>
              </div>
            </div>
            <div className="col-md-3 col-12">
              <div className="d-flex mb-2">
                <label className="text-left w-100 text-black common-label-text mr-2">
                  {t("Customer Name")}
                </label>
                <div className="input-group">
                  <NormalInput
                    value={custName}
                    name="custName"
                    onChange={this.handleChange}
                  />
                </div>
              </div>
              <div className="d-flex mb-2">
                <label className="text-left w-100 text-black common-label-text mr-2">
                  {t("Customer Code")}
                </label>
                <div className="input-group">
                  <NormalInput
                    value={custCode}
                    name="custCode"
                    onChange={this.handleChange}
                  />
                </div>
              </div>
            </div>

            <div className="col-md-3 col-12">
              <div className="d-flex mb-2">
                <label className="text-left w-100 text-black common-label-text mr-2">
                  {t("Transaction Code")}
                </label>
                <div className="input-group">
                  <NormalInput
                    value={transCode}
                    name="transCode"
                    onChange={this.handleChange}
                  />
                </div>
              </div>
              <div className="d-flex mb-2">
                <label className="text-left w-100 text-black common-label-text mr-2">
                  {t("Transaction Type")}
                </label>
                <div className="input-group">
                  <NormalSelect
                    placeholderrequired={false}
                    options={transtypeOptions}
                    value={t(transtype)}
                    iconname="icon-down-key"
                    name="transtype"
                    onChange={this.handleChange}
                  />
                </div>
              </div>
            </div>

            <div className="col-md-3 col-12">
              <div className=" w-100 mb-2">
                <NormalButton
                  buttonClass={"mx-2"}
                  mainbg={true}
                  className="confirm"
                  label="Search"
                  outline={false}
                  onClick={this.handleSearch}
                />
              </div>
              <div className="d-flex w-100 mb-2">
                <label className="text-left w-50 fw-500 common-label-text p-2">
                  {t("Total Amount")}
                </label>
                <div className="">
                  <label className="text-left w-100 fw-500 common-label-text p-2">
                    $
                    {this.state.TotalTransactionAmount
                      ? this.state.TotalTransactionAmount
                      : 0}
                  </label>
                </div>
              </div>
            </div>
          </div>

          <div className="billing-table">
            <div className="py-4">
              <div className="table-container">
                <TableWrapper
                  headerDetails={headerDetails}
                  queryHandler={this.handlePagination}
                  pageMeta={pageMeta}
                >
                  {billingList
                    ? billingList.map((dataitem, index) => {
                        let {
                          icon,
                          id,
                          sa_custno,
                          sa_custname,
                          sa_date,
                          sa_totamt,
                          sa_transacno_ref,
                          void_refno,
                          sa_staffname,
                          sa_status,
                          sa_transacno_type,
                          sa_transacno,
                          payment_remarks,
                          isvoid,
                          no_of_qty,
                          no_of_lines,
                          or,
                          paid_amount,
                          total_amount,
                          item,
                          new_date,
                        } = dataitem;
                        return (
                          <tr key={index}>
                            {/* {Number(this.props.tokenDetails.role_code) == 1 ||
                            Number(this.props.tokenDetails.role_code) == 2 ? (
                              <td className="position-relative">
                                <NormalButton
                                  buttonClass={"mx-0"}
                                  mainbgrev={true}
                                  className="confirm fs-11"
                                  label={t("Change Price")}
                                  outline={false}
                                  onClick={() =>
                                    history.push(
                                      `/admin/transactionhistory/pricechange/${id}`
                                    )
                                  }
                                  disabled={!isvoid ? false : true}
                                />
                              </td>
                            ) : (
                              ""
                            )} */}

                            <td>
                              <div className="text-left">{sa_custno}</div>
                            </td>
                            <td>
                              <div className="text-left">{sa_custname}</div>
                            </td>
                            <td>
                              <div className="text-right">{sa_date}</div>
                            </td>
                            <td className="d-none">
                              <div className="text-right">{new_date}</div>
                            </td>
                            <td>
                              <div className="text-right">{sa_totamt}</div>
                            </td>
                            <td className="d-none">
                              <div className="text-right">{total_amount}</div>
                            </td>
                            <td className="d-none">
                              <div className="text-right">{paid_amount}</div>
                            </td>
                            <td className="d-none">
                              <div className="text-left">{item}</div>
                            </td>
                            <td>
                              <div className="text-left">
                                {sa_transacno_ref}
                              </div>
                            </td>
                            <td>
                              <div className="text-left">{void_refno}</div>
                            </td>
                            <td>
                              <div className="text-left">{sa_staffname}</div>
                            </td>
                            <td className="d-none">
                              <div className="text-right">{no_of_qty}</div>
                            </td>
                            <td className="d-none">
                              <div className="text-right">{no_of_lines}</div>
                            </td>
                            <td className="d-none">
                              <div className="text-right">{or}</div>
                            </td>

                            <td>
                              <div className="text-left">{sa_status}</div>
                            </td>
                            <td>
                              <div className="text-left">
                                {sa_transacno_type}
                              </div>
                            </td>
                            <td>
                              <div className="text-left">{payment_remarks}</div>
                            </td>
                            <td className="position-relative">
                              <div className="d-flex justify-content-center">
                                <NormalButton
                                  buttonClass={"mx-0"}
                                  mainbgrev={true}
                                  className="confirm fs-11"
                                  label={t("View")}
                                  outline={false}
                                  onClick={() =>
                                    history.push(
                                      `/admin/transactionhistory/print/bill/${sa_transacno}`
                                    )
                                  }
                                />
                                {(Number(this.props.tokenDetails.role_code) ==
                                  1 ||
                                  Number(this.props.tokenDetails.role_code) ==
                                    2) &&
                                settingData.showChangePrice ? (
                                  <NormalButton
                                    buttonClass={"mx-0 ml-1"}
                                    mainbgrev={true}
                                    className="confirm fs-11"
                                    label={t("Change Price")}
                                    outline={false}
                                    onClick={() =>
                                      history.push(
                                        `/admin/transactionhistory/pricechange/${id}`
                                      )
                                    }
                                    disabled={!isvoid ? false : true}
                                  />
                                ) : (
                                  ""
                                )}
                              </div>
                            </td>
                          </tr>
                        );
                      })
                    : null}
                </TableWrapper>
              </div>
            </div>
          </div>
        </div>
      </>
    );
  }
}

const mapStateToProps = state => ({
  tokenDetails: state.authStore.tokenDetails,
});

const mapDispatchToProps = dispatch => {
  return bindActionCreators(
    {
      getCommonApi,
      commonCreateApi,
    },
    dispatch
  );
};

export const TransactionHistory = withTranslation()(
  connect(mapStateToProps, mapDispatchToProps)(TransactionHistoryClass)
);
