  render() {
    let { headerDetails, pageMeta, quotationList, formField, isLoading } =
      this.state;

    let {
      fromDate,
      toDate,
      custName,
      workorderinv_number,
      projectName,
      status,
    } = formField;

    let { t } = this.props;

    return (
      <div className="project-section col-md-12 align-items-center">
        <div className="col-md-12 d-flex">
          <p className="label-head mb-4">{t("Work Order Invoice")}</p>
