import React, { Component } from "react";
import {
  NormalInput,
  NormalButton,
  TableWrapper,
  NormalTextarea,
  NormalSelect,
  NormalCheckbox,
  NormalModal,
} from "component/common";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import {
  getCommonApi,
  commonPatchApi,
  commonCreateApi,
  commonUpdateApi,
} from "redux/actions/common";
import "./style.scss";
import { Toast } from "service/toast";
import closeIcon from "assets/images/close.png";
import { CourseTreatmentDone } from "./coursetreatmentDone";
import { withTranslation } from "react-i18next";
import _ from "lodash";

export class CoursePopupClass extends Component {
  state = {
    headerDetails: [
      { label: "No." },
      { label: "Program" },
      { label: "Next appt" },
      { label: "Amount" },
    ],
    data_list: {
      deposit: "",
      discount: "",
      discount_amt: "",
      discount_price: "",
      free_sessions: null,
      id: null,
      itemdesc: "",
      price: "",
      quantity: null,
      tmp_treatment: [],
      total_price: "",
      trans_amt: "",
      treatment_no: null,
      outstanding: null,
      disc_reason: "182",
      discreason_txt: "",
      autoProportion: false,
