  handleDepositAmount = () => {
    let { data_list } = this.state;
    let { trans_amt, deposit, outstanding } = data_list;
    data_list["outstanding"] = Number(trans_amt - deposit).toFixed(2);
    this.setState({
      data_list,
    });
