  handleDialog = async () => {
    let { isOpenTreatmentDone } = this.state;

    isOpenTreatmentDone = false;

    await this.setState({
      isOpenTreatmentDone,
      editCart: {},
    });
    this.getCourseAfterTreatmentDoneConfirm();
