  handleChange = async ({ target: { value, name } }) => {
    let {
      data_list,
      deposit,
      is_percentage,
      discount_price_txt,
      total_price,
      outstanding,
    } = this.state;

    if (name === "discount_price_txt") {
      discount_price_txt = value;
      this.setState({
        discount_price_txt,
      });
    } else if (name == "is_percentage") {
      is_percentage = value;
      this.setState({
        is_percentage,
      });
    } else if (name === "disc_reason" && Number(value) !== 182) {
      data_list["discreason_txt"] = "";
      data_list["disc_reason"] = value;
      this.setState({
        data_list,
      });
    } else if (name === "disc_reason" && Number(value) === 182) {
      data_list["disc_reason"] = value;
      this.setState({
        data_list,
      });
    } else {
      data_list[name] = value;
      await this.setState({
        data_list,
      });
    }
  };
