                      </div>
                    </div>
                    <div className="col-md-4 mb-2"></div>
                    <div className="col-md-4 col-12 mb-2 text-right">
                      <label className=" text-black common-label-text">
                        Outstanding
                      </label>
                    </div>
                    <div className="col-md-4 col-12 pb-1 pt-1 mb-2">
                      <div className="input-group">
                        <NormalInput
                          placeholder=""
                          value={outstanding ? outstanding : ""}
                          type="number"
                          name="outstanding"
                          onChange={() => {}}
                          disabled
                        />
                      </div>
                    </div>
                    <div className="col-md-4 mb-2"></div>
                  </div>
                </div>
              </div>
              <div className="col-md-7 col-12 mb-3 ">
                <div className="p-2 border">
                  <div className="d-flex flex-wrap mb-2">
                    <div className="col-md-2 col-12">
                      <label className="text-left text-black common-label-text pb-2">
                        Sessions
                      </label>
                    </div>
                    <div className="col-md-2 col-12">
                      <div className="input-group">
                        <NormalInput
                          placeholder=""
                          value={Sessions}
                          type="number"
                          name="Sessions"
                          onChange={this.handleChange}
                          disabled={is_done ? false : true}
                        />
                      </div>
                    </div>
                    <div className="col-md-2 col-12">
                      <NormalButton
                        mainbg={true}
                        label="TD"
                        onClick={this.handleTreatmentDone}
                      />
                    </div>
                  </div>
                  <div className="d-flex flex-wrap mb-2 mt-2">
                    <div className="col-md-7 justify-content-start col-12 mr-5 mt-2">
                      <label className="fw-500">TD Staff List</label>
                    </div>

                    <div className="col-md-4 col-12 justify-content-end"></div>
                  </div>
                  <div className="d-flex">
                    <div className="col-6">
                      <label className="text-left text-black common-label-text pb-2 fw-500">
                        {`Staff`}
                      </label>
                    </div>
                    <div className="col-2 text-center">
                      <label className="text-black common-label-text pb-2 fw-500">
                        {`Session`}
                      </label>
                    </div>
                    <div className="col-2 text-center">
                      <label className="text-black common-label-text pb-2 fw-500">
                        {`WP`}
                      </label>
                    </div>
                  </div>
                  <div className="d-flex flex-wrap">
                    <div className="col-md-12 w-100 bg-light p-3 border">
                      {sessionStaff &&
                        sessionStaff.length > 0 &&
                        sessionStaff.map((item, index) => {
                          return (
                            <div className="row" key={index}>
                              <div className="col-6 text-left ">
                                <label className="text-black common-label-text pb-2">
                                  {item.name}
                                </label>
                              </div>
                              <div className="col-2 text-right">
                                <label className="text-black common-label-text pb-2">
                                  {Number(item.session).toFixed(2)}
                                </label>
                              </div>
                              <div className="col-2 text-right">
                                <label className="align-self-end text-right text-black common-label-text pb-2">
                                  {Number(item.wp1).toFixed(2)}
                                </label>
                              </div>
                            </div>
                          );
                        })}
                      {!sessionStaff || sessionStaff.length <= 0 ? (
                        <div className="col-12">No record found</div>
                      ) : null}
                    </div>
                  </div>
                </div>
                <div className="p-2 mt-3 border">
                  <div className="d-flex flex-wrap mb-2">
                    <div className="col-md-2 col-12">
                      <label className="text-left text-black common-label-text pb-2">
                        Discount
                      </label>
                      <div className="input-group">
                        <NormalInput
                          placeholder=""
                          value={discount_price_txt}
                          type="number"
                          name="discount_price_txt"
                          onChange={this.handleChange}
                          disabled={
                            tmp_treatment.length < 1 || is_done ? false : true
                          }
                        />
                      </div>
                    </div>
                    <div className="col-md-2 col-12">
                      <div className="row">
                        <label className="text-left text-black common-label-text pb-2">
                          Type
                        </label>
                        <div className="input-group">
                          <NormalSelect
                            className={`col`}
                            options={discType}
                            value={is_percentage}
                            name="is_percentage"
                            onChange={this.handleChange}
                            disabled={is_total ? true : false}
                          />
                        </div>
                      </div>
                    </div>

                    <div className="col-md-4 col-12">
                      <label className="text-left text-black common-label-text pb-2">
                        Disc. Reason
                      </label>
                      <div className="input-group">
                        <NormalSelect
                          options={discount_reason_options}
                          value={disc_reason}
                          name="disc_reason"
                          onChange={this.handleChange}
                          disabled={is_total ? true : false}
                        />
                      </div>
                    </div>
                    {disc_reason == "182" ? (
                      <div className="col-md-4 col-12">
                        <label className="text-left text-black common-label-text ">
                          Discount reason
                        </label>
                        <div className="input-group mb-2">
                          <NormalTextarea
                            value={discreason_txt}
                            name="discreason_txt"
                            onChange={this.handleChange}
                            className="w-100"
                            disabled={is_total ? true : false}
                          />
                        </div>
                      </div>
                    ) : (
                      ""
                    )}
                    <div className="col-md-4 mt-4 pt-2 col-12">
                      <NormalButton
                        mainbg={true}
                        className="col fs-10"
                        label="Apply Disc."
                        onClick={this.handleApplyDiscount}
                        disabled={
                          is_total || tmp_treatment.length < 0 ? true : false
                        }
                      />
                    </div>
                  </div>
                  <div className="d-flex flex-wrap mb-2 mt-2">
                    <div className="col-md-7 justify-content-start col-12 mr-5 mt-2">
                      <label className="fw-500">Discount List</label>
                    </div>
                    {discount_reasons && discount_reasons.length > 0 ? (
                      <div className="col-md-4 pt-2 col-12 justify-content-end">
                        <NormalButton
                          mainbg={true}
                          className="col fs-10"
                          label="Reset Disc."
                          onClick={this.handleResetDiscount}
                          disabled={is_total ? true : false}
                        />
                      </div>
                    ) : null}
                  </div>
                  <div className="d-flex flex-wrap">
                    <div className="col-md-12 w-100 bg-light p-3 border">
                      {discount_reasons &&
                        discount_reasons.length > 0 &&
                        discount_reasons.map((item, index) => {
                          return (
                            <div className="row" key={index}>
                              <div className="col-6">
                                <label className="text-left text-black common-label-text pb-2">
                                  {item.remark}
                                </label>
                              </div>
                              <div className="col-2">
                                <label className="text-left text-black common-label-text pb-2">
                                  {item.disc_per} %
                                </label>
                              </div>
                              <div className="col-2">
                                <label className="text-left text-black common-label-text pb-2">
                                  $ {item.disc_amt}
                                </label>
                              </div>
                            </div>
                          );
                        })}
                      {!discount_reasons || discount_reasons.length <= 0 ? (
                        <div className="row">No record found</div>
                      ) : null}
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <div className="d-flex flex-wrap w-100 mb-3 p-2 border">
              <div
                className="col-md-12 col-12"
                onClick={this.handleSummaryFlag}
              >
                <div className="d-flex">
                  <div className="h6 col-6 col-md-9 text-left">
                    Summary List
                  </div>
                  <div className="h6 col-6 col-md-3 text-right fs-18">
                    {summaryFlag ? <>&#8593;</> : <>&#8595;</>}
                  </div>
                </div>
              </div>
              <div className={`table mb-2" + ${summaryFlag ? "" : "d-none"}`}>
                <TableWrapper headerDetails={headerDetails}>
                  {tmp_treatment && tmp_treatment.length > 0 ? (
                    tmp_treatment.map((item, index) => {
                      let { slno, program, next_appt, unit_amount } = item;
                      return (
                        <tr key={index}>
                          <td>
                            <div className="d-flex align-items-center justify-content-center">
                              {slno}
                            </div>
                          </td>

                          <td>
                            <div className="d-flex align-items-center justify-content-center">
                              {program}
                            </div>
                          </td>
                          <td>
                            <div className="d-flex align-items-center justify-content-center">
                              {next_appt}
                            </div>
                          </td>
                          <td>
                            <div className="d-flex align-items-center justify-content-center">
                              {unit_amount}
                            </div>
                          </td>
                        </tr>
                      );
                    })
                  ) : (
                    <tr>
                      <td colSpan="12">
                        <div className="d-flex align-items-center justify-content-center">
                          {`No record found`}
                        </div>
                      </td>
                    </tr>
                  )}
                </TableWrapper>
              </div>
            </div>
            <NormalModal
              className={"transaction-done-modal"}
              style={{ minWidth: "60%" }}
              modal={isOpenTreatmentDone}
              handleModal={this.handleDialog}
            >
              <img
                onClick={this.handleDialog}
                className="close cursor-pointer"
                src={closeIcon}
                alt=""
              />
              <CourseTreatmentDone
                id={cartId}
                cartId={this.props.cartId}
                sessionCount={data_list.Sessions}
                depositAmt={data_list.deposit}
                handleModal={this.handleDialog}
              ></CourseTreatmentDone>
            </NormalModal>
          </div>
        </div>
      </>
    );
  }
}

const mapStateToProps = state => ({});

const mapDispatchToProps = dispatch => {
  return bindActionCreators(
    {
      getCommonApi,
      commonPatchApi,
      commonCreateApi,
      commonUpdateApi,
    },
    dispatch
  );
};
export const CoursePopup = connect(
  mapStateToProps,
  mapDispatchToProps
)(CoursePopupClass);
