import React, { Component } from "react";
import "./style.scss";
// import { Appointments, TreatmentHistory, PurchaseHistory, PersonalDetails, Favourites } from './Details'
import classnames from "classnames";
import { Nav, NavItem, NavLink, TabContent, TabPane } from "reactstrap";
import { NormalInput, NormalButton, TableWrapper } from "component/common";

export class treatmentHistory extends Component {
  state = {
    activeTab: "1",
    isOpenTreatmentDone: false,
    isActiveTab: "detail",
    headerDetails: [
      {
        label: "Date",
        sortKey: false,
        width: "180px",
        divClass: "justify-content-end text-right",
      },
      { label: "Transaction #", width: "32px" },
      { label: "Treatment #", sortKey: false, width: "75px" },
      { label: "Discription", sortKey: false, width: "55px" },
      {
        label: "Payment",
        sortKey: false,
        width: "70px",
        divClass: "justify-content-end text-right",
      },
      {
        label: "Credit balance",
        sortKey: false,
        width: "72px",
        divClass: "justify-content-end text-right",
      },
      {
        label: "Outstanding",
        sortKey: false,
        width: "72px",
        divClass: "justify-content-end text-right",
      },
    ],
    cartList: [{}, {}, {}, {}],
  };

  toggle = tab => {
    if (this.state.activeTab !== tab) {
      this.setState({
        activeTab: tab,
      });
    }
  };

  render() {
    let { treatmentList, headerDetails, cartList } = this.state;
    return (
      <div className="treatment-account row">
        <div className="col-6 mt-3 mb-4">
          <div className="row">
            <div className="col-5 mb-2"></div>
            <div className="col-5 mb-2"></div>
            <div className="col-5">Total treatment count </div>
            <div className="col-5">4</div>
          </div>
        </div>
        <div className="col-6 mt-3 mb-4">
          <div className="row">
            <div className="col-6 mb-2">Credit balance</div>
            <div className="col-6 mb-2">$4567</div>

            <div className="col-6">Outstanding balance</div>
            <div className="col-6">$500</div>
          </div>
        </div>
        <div className="col-12">
          <div className="table">
            <TableWrapper
              headerDetails={headerDetails}
              queryHandler={this.handlePagination}
              // pageMeta={pageMeta}
              // isEmpty={cartList.length === 0 ? true:false}
            >
              {cartList.length > 0
                ? cartList.map((item, index) => {
                    return (
                      <tr key={index}>
                        <td className="position-relative status-type">
                          <span className={``}></span>
                          <div className="text-right">{"12/12/2020"}</div>
                        </td>
                        <td>
                          <div className="text-left">{"ASDFSAD34534"}</div>
                        </td>
                        <td>
                          <div className="text-left">{"TRTTRA1134"}</div>
                        </td>
                        <td>
                          <div className="text-left">{"Facial Fire"}</div>
                        </td>
                        <td>
                          <div className="text-right">{"$1500"}</div>
                        </td>
                        <td>
                          <div className="text-right">{"$1500"}</div>
                        </td>
                        <td>
                          <div className="text-right">{"$1500"}</div>
                        </td>
                      </tr>
                    );
                  })
                : ""}
            </TableWrapper>
          </div>
        </div>
        <div className="col-12 text-center">
          <NormalButton
            buttonClass={"print"}
            // mainbg={true}
            className="col-12 fs-15 "
            label="Print"
            // outline={false}
            onClick={() => this.setState({ isOpenEditDisc: false })}
          />
        </div>
      </div>
    );
  }
}
