import React, { Fragment } from "react";
import { Text, View, StyleSheet } from "@react-pdf/renderer";

const borderColor = "#90e5fc";
const styles = StyleSheet.create({
  row: {
    flexDirection: "row",
    borderBottomColor: "#bff0fd",
    borderBottomWidth: 1,
    height: 24,
    fontStyle: "bold",
    flexGrow: 1,
    fontSize: "9px",
    alignItems: "center",
  },
  description: {
    width: "20%",
    borderRightColor: borderColor,
    borderRightWidth: 1,
    textAlign: "center",
    overflow:"hidden"
  },
  date: { 
    width: "10%",
    borderRightColor: borderColor,
    borderRightWidth: 1,
    textAlign: "center",
  },
  status: {
    width: "13%",
    borderRightColor: borderColor,
    borderRightWidth: 1,
    textAlign: "center",
  },
  type: {
    width: "13%",
    borderRightColor: borderColor,
    borderRightWidth: 1,
    textAlign: "center",
  },
  prepaid: {
    width: "15%",
    borderRightColor: borderColor,
    borderRightWidth: 1,
    textAlign: "center",
  },
  payment: {
    width: "10%",
    borderRightColor: borderColor,
    borderRightWidth: 1,
    textAlign: "right",
  },
});

export class PrepaidTable extends React.Component {
  render() {
    let { items } = this.props;
    return items.map((item, index) => (
      <View style={styles.row} key={index}>
        <Text style={styles.prepaid}>{item.prepaid}</Text>
        <Text style={styles.prepaid}>{item.pp_no}</Text>
        <Text style={styles.description}>{item.pp_desc}</Text>
        <Text style={styles.date}>{item.last_update}</Text>
        <Text style={styles.date}>{item.sa_date}</Text>
        <Text style={styles.type}>{item.type}</Text>
        <Text style={styles.status}>{item.exp_date}</Text>
        <Text style={styles.status}>{item.exp_status}</Text>
        <Text style={styles.payment}>{item.pp_total}</Text>
        <Text style={styles.payment}>{item.topup_amt}</Text>
      </View>
    ));
  }
}
