import React, { Component } from "react";
import {
  NormalButton,
  TableWrapper,
  InputSearch,
  NormalDate,
  NormalSelect,
  NormalCheckbox,
  NormalInput,
  NormalTimePicker,
  NormalModal,
} from "component/common";
//import "./style.scss";
import { AiOutlinePlus, AiOutlineMinus } from "react-icons/ai";
import { withTranslation } from "react-i18next";
import { Departmentpopup } from "../Itemdataentry/popup/department";
import { Brandpopup } from "../Itemdataentry/popup/brand";
import { Classpopup } from "../Itemdataentry/popup/gclass";
import { Newuompopup } from "../Itemdataentry/popup/adduom";
import { Newlinkpopup } from "../Itemdataentry/popup/addlink";
import { Editlinkpopup } from "../Itemdataentry/popup/editlink";
import closeIcon from "assets/images/close.png";
import { Toast } from "service/toast";
import { RiDeleteBin5Line } from "react-icons/ri";
import { FaRegHandPointUp } from "react-icons/fa";
import { BsPencilSquare } from "react-icons/bs";
import { BiPencil } from "react-icons/bi";
import { DragFileUpload } from "../../../common";
import { displayImg } from "service/helperFunctions";
import { createStaffPlus } from "redux/actions/staffPlus";
import {
  ItemDivs,
  ItemUom,
  ItemSitelists,
  NewItemUomprices,
  VoucherConditions,
  ItemBatches,
  getStocks,
  ItemBrands,
  NewPrepaidOpenConditions,
  ItemSupplies,
  ItemStocklists,
  TaxType2TaxCodes,
  NewItemLinks,
  TaxType1TaxCodes,
  ItemDepts,
  NewItemRanges,
  PrepaidOpenConditions,
  ItemUomprices,
  ItemClasses,
  CommGroupHdrs,
  ItemLinks,
  ItemRanges,
  ItemTypes,
  NewStocks,
  PackageDtls,
  PackageItemDetails,
  PackageHdrs,
  NewPackageHdrs,
  Itemusagelists,
  NewItemusagelists,
  updateCommon,
} from "redux/actions/Backend";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";

export class EditItemClass extends Component {
  state = {
    stkbalanceDetails: [{ label: "No" }, { label: "Qty" }],
    LinkcodeDetails: [
      { label: "Link Code" },
      { label: "Link Description" },
      { label: "Rpt Code" },
      { label: "Action" },
    ],
    StockDetails: [
      { label: "Store Code" },
      { label: "Store Description" },
      { label: "All" },
    ],
    UOMoneDetails: [
      { label: "No" },
      { label: "UOMC Code" },
      { label: "UOMC Description" },
      { label: "=" },
      { label: "UOM Unit" },
      { label: "UOM Code" },
      { label: "UOM Description" },
      { label: "Action" },
    ],
    packageDetails: [
      { label: "StackName" },
      { label: "Division" },
      { label: "Dept" },
      { label: "Brand" },
      { label: "range" },
      { label: "UOM" },
      { label: "UOM unit" },
      { label: "Price" },
      { label: "  " },
    ],
    packagetwoDetails: [
      { label: "Item Code" },
      { label: "Description" },
      { label: "Qty" },
      { label: "U.Price" },
      { label: "Total" },
      { label: "Unit.Disc" },
      { label: "P.Price " },
      { label: "Total Amount" },
      { label: "UOM" },
      { label: "Active" },
      { label: "Action" },
    ],
    UOMtwoDetails: [
      { label: "No" },
      { label: "UOMC Desc" },
      { label: "Price" },
      { label: "Cost" },
      { label: "Min Margin %" },
      { label: "Action" },
    ],
    ItemoneDetails: [
      { label: "Description", sortKey: true },
      { label: "Item Code", sortKey: true },
      { label: "Bar code", sortKey: true },
      { label: "Action", sortKey: true },
    ],
    ItemtwoDetails: [
      { label: "Item Code" },
      { label: "Item Description" },
      { label: "Quality" },
      { label: "UOM" },
      { label: "Active" },
      { label: "Action" },
    ],
    prepaidDetails: [
      { label: "Type" },
      { label: "Condition Type 1" },
      { label: "Condition Type 2" },
      { label: "Amount" },
      { label: "Rate" },
      { label: "Active" },
      { label: "Action" },
    ],
    pageMeta: {},
    staffList: [],
    stocklist: [],
    option: [
      { label: 10, value: 10 },
      { label: 25, value: 25 },
      { label: 50, value: 50 },
      { label: 100, value: 100 },
    ],
    stock_type: [],
    validperiod: [
      { label: "30 days", value: 30 },
      { label: "60 days", value: 60 },
      { label: "90 days", value: 90 },
      { label: "180 days", value: 180 },
      { label: "360 days", value: 360 },
      { label: "720 days", value: 720 },
      { label: "1080 days", value: 1080 },
      { label: "120 days", value: 120 },
    ],
    Inclusive_type: [
      { label: "Product Only", value: "Product Only" },
      { label: "Service Only", value: "Service Only" },
      { label: "All", value: "All" },
    ],
    exclusive_type: [
      { label: "Product Only", value: "Product Only" },
      { label: "Service Only", value: "Service Only" },
    ],
    pricetracker: [
      { label: "Item Code" },
      { label: "Item Description" },
      { label: "Date" },
      { label: "Time" },
      { label: "Isactive" },
      { label: "Price" },
    ],
    runing_no: null,
    Division: [],
    Uoms: [],
    sitegroup: [],
    count: 10,
    subitemusage: [],
    stock_data: [],
    isoption: false,
    isgeneral: false,
    validvoucherdate: new Date(),
    idval: null,
    isstk: false,
    supply_itemsval: null,
    isstock: false,
    reoreder_level: false,
    prepaidamount: 0.0,
    itemClassIdId: null,
    taxone: null,
    taxtwo: null,
    itemDeptIdId: null,
    itemDivIdId: null,
    istaxcode: true,
    itemRangeIdId: null,
    itemTypeIdId: 3,
    itemusage_qty: null,
    itemusage_des: null,
    taxoneop: [],
    taxtwoop: [],
    itemusage_uom: null,
    itemusage_code: null,
    customer_replan: false,
    islink: false,
    isvoucher: false,
    isopenrange: false,
    isaccode: false,
    min_qty: 0,
    Replenishment: 0,
    Remind_advance: 0,
    isuom: false,
    isitem: false,
    isprepaid: false,
    images: null,
    stockdivision: null,
    dept: null,
    brand: null,
    account_no: null,
    addrangeoption: [],
    linkcount: null,
    stockname: null,
    stocktype: "SINGLE",
    range_desc: null,
    range_active: false,
    range_brand: null,
    range_code: null,
    stockprice: null,
    uomprice: null,
    uomcost: null,
    uommargin: null,
    floorprice: null,
    disclimit: 0,
    stockclass: null,
    range: null,
    description: null,
    duration: null,
    cost: null,
    sitecode: null,
    uomcode: null,
    validity: null,
    vouchervalue: 0,
    isOpendepartment: false,
    isOpenbrand: false,
    isOpenclass: false,
    isopenlinkedit: false,
    isopenitemedit: false,
    editid: null,
    editval: null,
    isOpenuom: false,
    isOpenlink: false,
    prepaidamountone: 0.0,
    valid: null,
    inclusivetype: [],
    brandlist: [],
    inclusive: null,
    card_noacc: false,
    exclusive: null,
    supplyitem: [],
    depts: [],
    uomsde: [],
    prepaidinclusive: null,
    prepaidexclusive: null,
    classoption: [],
    vouchervalid: false,
    prepaid_inclusive: [],
    prepaid_exclusive: [],
    iscommission: false,
    itemusage: [],
    prepaidall: false,
    prepaidprice: null,
    rangeoption: [],
    prepaidftable: [],
    linklist: [],
    percent: true,
    auto_cust_disc: true,
    tax: false,
    allow_foc: false,
    open_prepaid: false,
    commissionable: false,
    redeem_item: false,
    item_active: true,
    salescommissiongroup: [],
    workcommissiongroup: [],
    work_point: 0,
    Sales_commission: "",
    work_commission: "",
    sales_point: 0,
    item_desc: null,
    start_time: null,
    to_time: null,
    from_date: new Date(),
    to_date: new Date().setFullYear(new Date().getFullYear() + 3),
    Appt_TDT: false,
    appt: null,
    package_dept: [],
    packagedeptvalue: null,
    package_details: [],
    package_code: null,
    package_name: null,
    package_uom: null,
    package_price: null,
    package_qty: null,
    package_discount: 0,
    content_total: null,
    disc_amount: null,
    package_total: null,
    salon: true,
    retail: false,
    package_content: [],
    evenly_method: true,
    manual_method: false,
    disc_method: null,
    item_seq: null,
    search: "",
    filterdata: [],
    itemusage_barcode: null,
    itemusage_dept: null,
    itemusage_div: null,
    itemId: null,
    temp_maincontent: [],
    searchone: "",
    filterdataone: [],
    rptcode: null,
    ishistory: false,
    tracker: [],
  };
  componentDidMount = () => {
    let { itemId } = this.state;
    itemId = window.location.pathname.split("/")[4];
    this.setState({ itemId });
    console.log(itemId);
    this.Listofstocks({});
    this.Listofmenu({});
    this.listuoms({});
    this.listofsitegropus({});
    this.listofStockListing({});
    // this.listofinclusive({});
    this.listofbrand({});
    this.listofsupply({});
    this.listofitemusage({});
    this.listofclasses({});
    this.listofcommonhrds({});
    this.listtaxone({});
    this.listtaxtwo({});
    this.listofpackagedtl({});
    this.listofdept({});
    this.Listofitemtype({});
    this.Listofitemrange({});
    this.listprepaid({});
    this.listoflinklistitem({});
    this.listofsubitemuage({});
    this.listofedituomlist({});
    this.packagehrdsvalues({});
    this.packageDetailsvalue({});
  };

  updateState = (data) => {
    if (this.state.isMounted) this.setState(data);
  };

  Listofstocks = async () => {
    await this.props.getStocks().then((res) => {
      console.log(res);
      let { stock_data, temp_maincontent } = this.state;
      let objIndex = res.findIndex((obj) => obj.itemCode == this.state.itemId);
      temp_maincontent = res[objIndex];
      console.log(temp_maincontent);
      stock_data = res;
      console.log(stock_data);
      this.setState({
        stock_data,
      });
      console.log(this.state.itemId);
      this.assignall(temp_maincontent);
    });
  };

  assignall = (data) => {
    let {
      stockdivision,
      dept,
      brand,
      control_no,
      stockname,
      stocktype,
      stockprice,
      floorprice,
      supply_itemsval,
      disclimit,
      commissionable,
      redeem_item,
      stockclass,
      range,
      item_desc,
      duration,
      cost,
      percent,
      auto_cust_disc,
      open_prepaid,
      tax,
      allow_foc,
      item_seq,
      taxone,
      taxtwo,
      uomcost,
      uommargin,
      item_active,
      Replenishment,
      Remind_advance,
      Sales_commission,
      work_commission,
      reoreder_level,
      min_qty,
      sales_point,
      work_point,
      vouchervalue,
      vouchervalid,
      validity,
      prepaidamountone,
      prepaidamount,
      valid,
      account_no,
      itemClassIdId,
      itemDeptIdId,
      itemDivIdId,
      itemRangeIdId,
      itemTypeIdId,
      tracker,
    } = this.state;
    control_no = data.itemCode;
    stockdivision = data.itemDiv;
    dept = data.itemDept;
    brand = data.itemBrand;
    stockname = data.itemName;
    stocktype = data.itemType;
    supply_itemsval = data.itemSupp;
    disclimit = data.disclimit;
    commissionable = data.commissionable;
    stockclass = data.itemClass;
    auto_cust_disc = data.autocustdisc;
    range = data.itemRange;
    item_desc = data.itemDesc;
    item_seq = data.item_seq;
    allow_foc = data.isAllowFoc;
    tax = data.isHaveTax;
    taxone = data.t1TaxCode;
    taxtwo = data.t2TaxCode;
    uomcost = data.onhandCst;
    uommargin = data.itemMargin;
    item_active = data.itemIsactive;
    Replenishment = data.custReplenishDays;
    Remind_advance = data.custAdvanceDays;
    Sales_commission = data.salescomm;
    work_commission = data.workcomm;
    reoreder_level = data.reorderActive;
    min_qty = data.reorderMinqty;
    sales_point = data.salescommpoints;
    work_point = data.workcommpoints;
    vouchervalue = data.voucherValue;
    vouchervalid = data.voucherValueIsAmount;
    validity = data.voucherValidPeriod;
    prepaidamountone = data.prepaidValue;
    prepaidamount = data.prepaidSellAmt;
    valid = data.prepaidValidPeriod;
    account_no = data.accountCodeTd;
    itemClassIdId = data.itemClassIdId;
    itemDeptIdId = data.itemDeptIdId;
    itemDivIdId = data.itemDivIdId;
    itemRangeIdId = data.itemRangeIdId;
    itemTypeIdId = data.itemTypeIdId;
    stockprice = data.itemprice;
    let temp = {
      item_code: data.itemCode,
      item_desc: data.itemName,
      stockprice: data.itemprice,
    };

    tracker.push(temp);
    this.setState({
      tracker,
      stockdivision,
      dept,
      brand,
      control_no,
      stockname,
      stocktype,
      stockprice,
      floorprice,
      supply_itemsval,
      disclimit,
      commissionable,
      redeem_item,
      stockclass,
      range,
      item_desc,
      duration,
      cost,
      percent,
      auto_cust_disc,
      open_prepaid,
      tax,
      allow_foc,
      item_seq,
      taxone,
      taxtwo,
      uomcost,
      uommargin,
      item_active,
      Replenishment,
      Remind_advance,
      Sales_commission,
      work_commission,
      reoreder_level,
      min_qty,
      sales_point,
      work_point,
      vouchervalue,
      vouchervalid,
      validity,
      prepaidamountone,
      prepaidamount,
      valid,
      account_no,
      itemClassIdId,
      itemDeptIdId,
      itemDivIdId,
      itemRangeIdId,
      itemTypeIdId,
    });
    console.log(dept, range);
  };

  listprepaid = async () => {
    await this.props.PrepaidOpenConditions().then((res) => {
      console.log(res);
      let { prepaidftable, validity, prepaidamountone, prepaidamount } =
        this.state;
      let objIndex = res.findIndex((obj) => obj.itemCode == this.state.itemId);
      console.log(res[objIndex]);
      if (objIndex != -1) {
        prepaidftable = res[objIndex];
        validity = res[objIndex].prepaidValidPeriod;
        prepaidamount = res[objIndex].prepaidSellAmt;
        prepaidamountone = res[objIndex].prepaidValue;
      }
      console.log(prepaidftable);
      this.setState({
        prepaidftable,
        validity,
        prepaidamountone,
        prepaidamount,
      });
      console.log(prepaidftable.length);
    });
  };
  listofpackagedtl = async () => {
    await this.props.PackageItemDetails().then((res) => {
      console.log(res);
      let { package_details } = this.state;

      package_details = res;
      console.log(package_details);
      this.setState({
        package_details,
      });
      console.log(package_details.length);
    });
  };

  listofcommonhrds = async () => {
    let { salescommissiongroup, workcommissiongroup } = this.state;
    await this.props.CommGroupHdrs().then((res) => {
      for (let key of res) {
        if (key.type == "Sales") {
          salescommissiongroup.push({
            value: key.code,
            label: key.description,
            id: key.id,
          });
