        </NormalModal>
        <NormalModal
          className={"payment-modal"}
          style={{ minWidth: "40%" }}
          modal={isopenlinkedit}
          handleModal={this.handleEditlinkDialog}
        >
          <img
            onClick={this.handleEditlinkDialog}
            className="close cursor-pointer"
            src={closeIcon}
            alt=""
          />

          <Editlinkpopup
            handleModal={this.handleEditlinkDialog}
            name={editval}
            code={editid}
            listoflinklist={this.listofEditlinklist}
          ></Editlinkpopup>
        </NormalModal>

        <NormalModal
          className={"payment-modal"}
          style={{ minWidth: "40%" }}
          modal={isopenitemedit}
          handleModal={this.handleEdititemDialog}
        >
          <img
            onClick={this.handleEdititemDialog}
            className="close cursor-pointer"
            src={closeIcon}
            alt=""
          />
          <div>
            <h6>Add</h6>
            <div className="mt-4 d-flex">
              <div className="col-6">
                <p>Item Code</p>
                <NormalInput
                  value={itemusage_code}
                  name="item_code"
                  onChange={this.Item_Code}
                />
              </div>
              <div className="col-6">
                <span>Item Description</span>
                <span style={{ color: "red" }}>*</span>
                <NormalInput
                  value={itemusage_des}
                  name="itemusage_des"
                  onChange={this.Item_Code}
                />
              </div>
            </div>
            <div className="mt-4 d-flex">
              <div className="col-6">
                <p>UOM</p>
                <NormalSelect
                  value={itemusage_uom}
                  options={Uoms}
                  name="itemusage_uom"
                  onChange={this.Item_Code}
                />
              </div>
              <div className="col-6">
                <span>Qty</span>
                <span style={{ color: "red" }}>*</span>
                <NormalInput
                  value={itemusage_qty}
                  name="itemusage_qty"
                  onChange={this.Item_Code}
                />
              </div>
            </div>
            <div className="d-flex mt-3 justify-content-between">
              <div className="pl-2">
                <NormalButton
                  mainbg={true}
                  label={"Cancel"}
                  onClick={this.handleEdititemDialog}
                />
              </div>
              <div className="pr-2">
                <NormalButton
                  mainbg={true}
                  label={"Submit"}
                  onClick={() =>
                    this.Additemusagetable(
                      itemusage_code,
                      itemusage_des,
                      itemusage_barcode,
                      itemusage_dept,
                      itemusage_div,
                      itemusage_uom,
                      itemusage_qty
                    )
                  }
                />
              </div>
            </div>
          </div>
        </NormalModal>

        <NormalModal
          className={"payment-modal"}
          style={{ minWidth: "50%" }}
          modal={isopenrange}
          handleModal={this.handlerangeDialog}
        >
          <img
            onClick={this.handlerangeDialog}
            className="close cursor-pointer"
            src={closeIcon}
            alt=""
          />
          <div>
            <h6>Add New Range</h6>
            <div className="mt-4 d-flex">
              <div className="col-6">
                <span>Code</span>
                <span style={{ color: "red" }}>*</span>
                <NormalInput
                  value={range_code}
                  name="range_code"
                  type="number"
                  disabled={true}
                  onChange={this.handlerange}
                />
              </div>
              <div className="col-6">
                <span>Description</span>
                <span style={{ color: "red" }}>*</span>
                <NormalInput
                  value={range_desc}
                  name="range_desc"
                  onChange={this.handlerange}
                />
              </div>
            </div>
            <div className="mt-4 d-flex">
              <div className="col-6">
                <span>Brand</span>
                <span style={{ color: "red" }}>*</span>
                <NormalSelect
                  value={range_brand}
                  name="range_brand"
                  options={brandlist}
                  onChange={this.handlerange}
                />
              </div>
              <div className="col-6 d-flex mt-3">
                <NormalCheckbox
                  checked={range_active}
                  name="range_active"
                  onChange={this.handlerange}
                />
                <p>{t("Active")}</p>
              </div>
            </div>
            <div className="d-flex mt-3 justify-content-between">
              <div className="pl-2">
                <NormalButton
                  mainbg={true}
                  label={"Cancel"}
                  onClick={this.handlerangeDialog}
                />
              </div>
              <div className="pr-2">
                <NormalButton
                  mainbg={true}
                  label={"Submit"}
                  onClick={() =>
                    this.Addrangeitems(
                      range_code,
                      range_desc,
                      range_brand,
                      range_active
                    )
                  }
                />
              </div>
            </div>
          </div>
        </NormalModal>

        <NormalModal
          className={"payment-modal"}
          style={{ minWidth: "50%" }}
          modal={ishistory}
          handleModal={() => this.handlehistory()}
        >
          <img
            onClick={() => this.handlehistory()}
            className="close cursor-pointer"
            src={closeIcon}
            alt=""
          />
          <div>
            <h6>Price History Tracker</h6>
            <div className="tab-table-content">
              <div className="py-4">
                <div className="table-container">
                  <TableWrapper headerDetails={pricetracker}>
                    {is_loading ? (
                      <tr>
                        <td colSpan="7">
                          <div class="d-flex mt-5 align-items-center justify-content-center">
                            <div class="spinner-border" role="status">
                              <span class="sr-only">Loading...</span>
                            </div>
                          </div>
                        </td>
                      </tr>
                    ) : tracker.length > 0 ? (
                      tracker.map(({ item_code, item_desc, price }, index) => {
                        return (
                          <tr key={index}>
                            <td className="position-relative status-type">
                              <div className="d-flex align-items-center justify-content-center">
                                {item_code}
                              </div>
                            </td>
                            <td>
                              <div className="d-flex align-items-center justify-content-center">
                                {item_desc}
                              </div>
                            </td>
                            <td>
                              <div className="d-flex align-items-center justify-content-center">
                                {price}
                              </div>
                            </td>
                            <td>
                              <div className="d-flex align-items-center justify-content-center">
                                {}
                              </div>
                            </td>
                            <td>
                              <div className="d-flex align-items-center justify-content-center">
                                {}
                              </div>
                            </td>
                            <td>
                              <div className="d-flex align-items-center justify-content-center">
                                {price}
                              </div>
                            </td>
                          </tr>
                        );
                      })
                    ) : null}
                  </TableWrapper>
                </div>
              </div>
            </div>
          </div>
        </NormalModal>
      </>
    );
  }
}

const mapDispatchToProps = (dispatch) => {
  return bindActionCreators(
    {
      ItemDivs,
      ItemUom,
      ItemSitelists,
      VoucherConditions,
      ItemBrands,
      ItemSupplies,
      ItemDepts,
      ItemLinks,
      ItemUomprices,
      ItemTypes,
      ItemClasses,
      ItemStocklists,
      ItemRanges,
      getStocks,
      NewItemRanges,
      NewStocks,
      createStaffPlus,
      CommGroupHdrs,
      TaxType2TaxCodes,
      PrepaidOpenConditions,
      TaxType1TaxCodes,
      NewItemLinks,
      ItemBatches,
      updateCommon,
      NewPrepaidOpenConditions,
      PackageHdrs,
      NewItemUomprices,
      PackageDtls,
      PackageItemDetails,
      NewPackageHdrs,
      NewItemusagelists,
      Itemusagelists,
    },
    dispatch
  );
};

export const EditItem = withTranslation()(
  connect(null, mapDispatchToProps)(EditItemClass)
);
