    let {
      productCard = {},
      list,
      tstaffList = [],
      selectedYear,
      yearList,
      isOpenTreatmentDone,
      headerDetails,
      tsStaff,
      isOpenReversal,
      treatmentIds,
      activeMenu,
      isOpenExchangeService,
      menuOption,
      menuId,
      rangeOption,
      rangeId,
      isHandleExchangeConfirmation,
      ExchangeItem,
      SelectedExchangeItem,
    } = this.state;
    let localtStaffList = [...tstaffList];
