                    />
                  </div>
                  <div>
                    {this.validator.message(
                      t("Project"),
                      projectTitle,
                      t("required")
                    )}
                  </div>

                  
                </div>

                <div className="col-md-4 col-12">
                  <label className="text-left text-black common-label-text fs-17 pt-3">
                    {t("PO Date")}
                  </label>
                  <div className="input-group-normal">
                    <NormalDateTime
                    onChange={this.handleDatePick}
                    inputcol="p-0 inTime"
                    value={PODate ? new Date(PODate) : new Date()}
                    name="PODate"
                    //className="dob-pick"
                    showYearDropdown={true}
                    dateFormat="dd/MM/yyyy"
                    // maxDate={new Date(toDate)}
                    showDisabledMonthNavigation
                    />
                  </div>
                  <div>
                    {this.validator.message(
                      t("PO Date"),
                      PODate,
                      t("required")
                    )}
                  </div>

                  <label className="text-left text-black common-label-text fs-17 pt-3">
                    {t("Company Name")}
                  </label>
                  <div className="input-group-normal">
                    <NormalInput
                      placeholder="Enter here"
                      disabled={disableEdit}
                      value={companyName}
                      name="companyName"
                      onChange={this.handleChange}
                    />
                  </div>
                  <div>
                    {this.validator.message(
                      t("Company Name"),
                      companyName,
                      t("required")
                    )}
                  </div>

                  
                </div>
                
                <div className="col-md-4 col-12">
                  <label className="text-left text-black common-label-text fs-17 pt-3 mt-2">
                    {t("Status")}
                  </label>
                  <div className="input-group-normal">
                    <NormalSelect
                      options={statusOption}
                      
                      value={status}
                      name="status"
                      onChange={this.handleChange}
                    />
                  </div>
                  <div>
                    {this.validator.message(
                      t("Status"),
                      status,
                      t("required")
                    )}
                  </div>

                  <label className="text-left text-black common-label-text fs-17 pt-3">
                    {t("From")}
                  </label>
                  <div className="input-group-normal">
                    <NormalInput
                      placeholder="Enter here"
                      disabled={disableEdit}
                      value={from}
                      name="from"
                      onChange={this.handleChange}
                    />
                  </div>
                  <div>
                    {this.validator.message(
                      t("From"),
                      from,
                      t("required")
                    )}
                  </div>

                  
                  

                </div>
              </div>


              <div className="row">
                <div className="col-md-6 col-12">
                  <label className="text-left text-black common-label-text fs-17 pt-3">
                    {t("Remarks")}
                  </label>
                  <div className="input-group-desc">
                    <NormalTextarea
                      placeholder="Enter here"
                      disabled={disableEdit}
                      value={remarks}
                      name="remarks"
                      onChange={this.handleChange}
                    />
                  </div>
                </div>

                <div className="col-md-2 col-12"></div>

                <div className="col-md-4 col-12">
                <label className="text-left text-black common-label-text fs-17 pt-3">
                    {t("Quotation Number")}
                  </label>
                  <div className="input-group-normal">
                    <NormalInput
                      placeholder="Enter here"
                      disabled={disableEdit}
                      value={quoNumber}
                      name="quoNumber"
                      onChange={this.handleChange}
                    />
                  </div>
                  {/* <div>
                    {this.validator.message(
                      t("Quotation Number"),
                      quoNumber,
                      t("required")
                    )}
                  </div> */}
                </div> 
                


              </div>  
                
              <div className="col-md-12 PO-content">
                <div className="tab-menus">
                  <ul>
                    {navLinks.map(({ to, label, id }, index) => (
                      <li key={index}>
                        <NavLink to={to} className="nav-link">
                          <div
                            className={`sidebar-menu ${
                              currentValue === index ? "active" : ""
                            }`}
                            onClick={() => this.handleClick({ key: index, id: id })}
                          >
                            <span className="sidebar-menu-desc">{t(label)}</span>
                          </div>
                        </NavLink>
                      </li>
                    ))}
                  </ul>
                </div>
                {this.state.selectedMenu === "Details" && (
                  <Details
                    disableEdit = {disableEdit}
                    poId = {this.props.match.params.id}
                    quoId = {fkQuotation}
                    validator = {this.validator}
                    storeItemDetails = {this.storeItemDetails.bind(this)}
                    storedItemListStored = {this.state.storedItemListStored}
                    formFieldsDetailsStored = {this.state.formFieldsDetailsStored}
                    id={this.state.selectedMenu}
                    // search={formFields.search}
                    api={"details"}
                  />
                )}
                {this.state.selectedMenu === "Address" && (
                  <Address
                    disableEdit = {disableEdit}
                    storeAddressFields = {this.storeAddressFields.bind(this)}
                    formFieldsBillingStored = {this.state.formFieldsBillingStored}
                    formFieldsShippingStored = {this.state.formFieldsShippingStored}
                    id={this.state.selectedMenu}
                    // search={formFields.search}
                    api={"address"}
                  />
                )}
               
              </div>   

              <div className="row">
                <div className="col-md-6 col-12 mt-3"></div>
                
                <div className="col-md-2 col-12 mt-3">
                  <NormalButton
                    buttonClass={"mx-2 mb-3"}
                    mainbg={true}
                    className="confirm"
                    label="Cancel"
                    outline={false}
                    onClick={() => { 
                      if (window.confirm('Are you sure you want to leave this page and discard changes?'))
                      this.props.history.push(
                        `/admin/po`
                      )
                    }
                    }              
                  />
                </div>

                <div className="col-md-2 col-12 mt-3">
                  <NormalButton
                    buttonClass={"mx-2"}
                    mainbg={true}
                    className="confirm"
                    label="Save"
                    onClick={() => this.handleSubmit()}
                  />
                </div>
                  
                 
                <div className="col-md-2 col-12 mt-3">
                  <NormalButton
                    buttonClass={"mx-2 mb-3"}
                    mainbg={true}
                    className="confirm"
                    label="Post"
                    outline={false}
                    onClick={() => this.handleSubmit(status)}              
                  />
                </div>
                

              </div> 

              
                
              
            </div>
          </div>
        {/* )} */}
      </div>
      
    );
  }
}

const mapStateToProps = (state) => ({
  tokenDetail: state.authStore.tokenDetails,
});

const mapDispatchToProps = (dispatch) => {
  return bindActionCreators(
    {
      getCommonApi,
      commonCreateApi,
      commonUpdateApi,
      updatePO,
      createPO,
      deletePO,
      getTokenDetails
    },
    dispatch
  );
};

export const AddPO = withTranslation()(
  connect(mapStateToProps, mapDispatchToProps)(AddPOClass)
);
