  // filter: state.dashboard
});

const mapDispatchToProps = dispatch => {
  return bindActionCreators(
    {
      getStaffAvailability,
    },
    dispatch
  );
};

export const StaffPlusAvailability = withTranslation()(
  connect(mapStateToProps, mapDispatchToProps)(StaffPlusAvailabilityClass)
);
